// Format Input & Parse News
const input = $('Webhook - Carousel Gen').item.json;
let data = input.body || input;
if (typeof data === 'string') {
  try { data = JSON.parse(data); } catch(e) { data = input; }
}

// Handle news content specifically if present
const newsItems = data.news_items || [];
// If individual fields are passed instead of array
if (newsItems.length === 0 && (data.news_title || data.news_content)) {
    newsItems.push({
        title: data.news_title,
        content: data.news_content,
        source: data.news_source
    });
}

// Extract rich content
const news1 = newsItems[0] || {};
const news2 = newsItems[1] || {};
const news3 = newsItems[2] || {};

const getContent = function(n) {
  return (n.content || n.snippet || n.description || '').substring(0, 500);
};

return [{
  json: {
    // Original fields
    topic: data.topic || news1.title || 'Tech Trends',
    visual_style: 'Realistic Photography', // FORCE Realistic Photography
    headline: data.headline || news1.title || '',

    // News Parsing
    news_count: newsItems.length,
    n1_title: news1.title || '',
    n1_source: news1.source || '',
    n1_content: getContent(news1),
    n2_title: news2.title || '',
    n2_source: news2.source || '',
    n2_content: getContent(news2),
    n3_title: news3.title || '',
    n3_source: news3.source || '',
    n3_content: getContent(news3),

    color_primary: data.color_primary || '#207CE5',
    color_secondary: data.color_secondary || '#004AAD'
  }
}];

