// Parse input and get logo
const webhookData = $('Webhook').item.json;
const logoItem = $input.item;

// Get logo base64
let logoBase64 = '';
if (logoItem.binary) {
  const binaryKey = Object.keys(logoItem.binary)[0];
  try {
    const buffer = await this.helpers.getBinaryDataBuffer(0, binaryKey);
    logoBase64 = buffer.toString('base64');
  } catch (e) {
    const bd = logoItem.binary[binaryKey];
    if (bd.data && bd.data !== 'filesystem-v2') logoBase64 = bd.data;
  }
}
if (!logoBase64) throw new Error('Failed to get logo');

// Parse webhook body
let data = webhookData;
if (webhookData.body) {
  if (typeof webhookData.body === 'string') {
    try { data = JSON.parse(webhookData.body); } catch(e) { data = webhookData; }
  } else {
    data = webhookData.body;
  }
}

const slides = data.slides || [];
if (slides.length === 0) throw new Error('No slides provided');

const vs = data.visual_style || 'Modern 3D';
const pc = data.color_primary || '#207CE5';
const sc = data.color_secondary || '#004AAD';
const ac = data.color_accent || '#FFFDF1';
const dc = data.color_dark || '#2B2B2B';

// DETAILED STYLE INSTRUCTIONS - Same as content-generation-flow.json
let style = '';

if (vs.toLowerCase().includes('glass')) {
  style = `## GLASSMORPHISM STYLE
**SCENE:** Brand color (${pc}) to secondary color (${sc}) gradient background. THREE frosted glass panels arranged in layered composition with technology icons visible through translucent layers. Semi-transparent hexagonal shapes scattered. Subtle blur effects on glass edges. Connection lines in accent color (${ac}).

**COLOR PALETTE:**
- Primary: ${pc}
- Secondary: ${sc}
- Accent/Text: ${ac}
- Dark: ${dc}

**VISUAL ELEMENTS:**
1. Three frosted glass panels with blur effect
2. Technology icons in white on panels (gears, circuits, data symbols)
3. Semi-transparent hexagonal shapes (${ac} accents)
4. Connection lines between elements (${ac})
5. Subtle glow effects on panel edges
6. Professional rim lighting for depth`;

} else if (vs.toLowerCase().includes('3d') || vs.toLowerCase().includes('modern')) {
  style = `## MODERN 3D STYLE
**SCENE:** Brand color (${pc}) gradient background with floating 3D geometric objects. Glossy spheres, cubes, and abstract tech shapes with realistic reflections and shadows. Dramatic lighting from multiple angles creating depth. Professional, futuristic corporate aesthetic.

**COLOR PALETTE:**
- Primary: ${pc}
- Secondary: ${sc}
- Accent/Text: ${ac}
- Dark: ${dc}

**VISUAL ELEMENTS:**
1. Floating 3D geometric shapes (spheres, cubes, cylinders)
2. Glossy metallic surfaces with reflections
3. Dramatic rim lighting and shadows
4. Abstract tech objects (servers, nodes, connections)
5. Depth of field blur on background elements
6. Subtle particle effects or light rays`;

} else if (vs.toLowerCase().includes('iso')) {
  style = `## ISOMETRIC ARCHITECTURE STYLE
**SCENE:** Brand color (${pc}) background with isometric 3D architecture. Clean geometric buildings, server rooms, or tech infrastructure in isometric perspective. Flat colors with subtle gradients. Professional technical illustration style.

**COLOR PALETTE:**
- Primary: ${pc}
- Secondary: ${sc}
- Accent/Text: ${ac}
- Dark: ${dc}

**VISUAL ELEMENTS:**
1. Isometric buildings or server structures
2. Clean geometric shapes in isometric view
3. Network connection lines between structures (${ac})
4. Small human figures for scale (optional)
5. Subtle shadows for depth
6. Technical grid or blueprint elements`;

} else if (vs.toLowerCase().includes('data') || vs.toLowerCase().includes('hero')) {
  style = `## DATA HERO / VISUALIZATION STYLE
**SCENE:** Brand color (${pc}) background with prominent data visualizations as hero element. Charts, graphs, dashboards, or data flow diagrams as main visual. Clean, modern infographic aesthetic with professional corporate feel.

**COLOR PALETTE:**
- Primary: ${pc}
- Secondary: ${sc}
- Accent/Text: ${ac}
- Dark: ${dc}

**VISUAL ELEMENTS:**
1. Large chart or graph as hero element (bar, line, pie, or dashboard)
2. Data points and nodes with connection lines (${ac})
3. Glowing data indicators and metrics
4. Abstract data flow visualization
5. Clean grid or axis lines
6. Subtle animated feel (arrows, flow indicators)`;

} else if (vs.toLowerCase().includes('realistic') || vs.toLowerCase().includes('photo')) {
  style = `## PHOTOREALISTIC STOCK PHOTOGRAPHY
Generate a professional stock photograph that looks EXACTLY like it was shot by a commercial photographer.

**COLOR PALETTE TO USE:**
- Primary Brand Color: ${pc}
- Secondary Color: ${sc}
- Accent/Text Color: ${ac}
- Dark Color: ${dc}

### ABSOLUTE PROHIBITIONS
- NO glowing effects, NO neon colors
- NO surreal or fantasy elements
- NO floating objects (everything must obey gravity)
- EVERYTHING must look like it could exist in a real photograph

### GRADIENT OVERLAY
- BOTTOM edge: 90% SOLID with dark color (${dc})
- TOP edge: 100% TRANSPARENT
- Perfectly HORIZONTAL transition

### SUBJECT & APPEARANCE
- 1-2 professional people, age 30-42, tech industry professionals
- Natural expressions, realistic skin texture, visible pores
- Professional clothing: oxford shirts, quality sweaters, blazers

### LIGHTING
- Soft natural window light from camera-left (45-degree angle)
- Subtle shadows, subsurface scattering on skin
- Color temperature: slightly warm (5500-6000K)

### CAMERA SPECS
- 85mm f/1.4 portrait lens simulation
- Shallow depth of field, subtle film grain
- Natural, slightly desaturated colors`;

} else {
  style = `## DEFAULT CORPORATE STYLE
**SCENE:** Clean brand color (${pc}) gradient background. Professional corporate design with abstract geometric elements. Modern, minimal aesthetic suitable for social media.

**COLOR PALETTE:**
- Primary: ${pc}
- Secondary: ${sc}
- Accent/Text: ${ac}
- Dark: ${dc}

**VISUAL ELEMENTS:**
1. Solid brand color background or subtle gradient to secondary
2. Abstract geometric shapes (hexagons, circles, lines) in accent color
3. Technology icons relevant to the topic
4. Clean accent lines or borders (${ac})
5. Professional lighting effects
6. Subtle depth through shadows or glows`;
}

return [{
  json: {
    slides: slides,
    total: slides.length,
    style: style,
    visual_style: vs,
    topic: data.topic || 'Technology',
    ac: ac,
    dc: dc,
    logo_base64: logoBase64
  }
}];

// Generate SLIDE 1
const d = $input.item.json;
const slide = d.slides[0];

const prompt = `Create a 4:5 portrait Instagram carousel image.

${d.style}

=== SLIDE 1 of ${d.total} ===
HEADLINE: "${slide.headline}"
TOPIC: ${d.topic}

=== CRITICAL REQUIREMENTS ===
1. HEADLINE TEXT: Bold, clear "${slide.headline}" text in ${d.ac} color
2. TEXT AREA: Dark gradient (${d.dc}) behind headline for readability
3. MSI LOGO: Place the provided logo in BOTTOM RIGHT corner - DO NOT modify, resize or distort it
4. QUALITY: Professional Instagram-ready, high resolution
5. CONSISTENCY: This is slide 1 of ${d.total} - ALL slides MUST have identical visual style

=== DO NOT ===
- Do NOT add any text other than the headline
- Do NOT modify the MSI logo in any way
- Do NOT use a different color palette`;

return [{ json: { slide_num: 1, headline: slide.headline, subtext: slide.subtext || '', prompt: prompt, logo: d.logo_base64, remaining_slides: d.slides.slice(1), total: d.total, style: d.style, topic: d.topic, ac: d.ac, dc: d.dc, results: [] } }];

const prev = $('Slide 1 Prompt').item.json;
const resp = $input.item.json;

let imgData = '';
const candidates = resp.candidates || [];
for (const c of candidates) {
  for (const p of (c?.content?.parts || [])) {
    const d = p.inline_data || p.inlineData;
    if (d && d.data) { imgData = d.data; break; }
  }
  if (imgData) break;
}
if (!imgData) throw new Error('No image for slide 1');

return [{ json: { ...prev, img_data: imgData } }];

const prev = $('Extract 1').item.json;
const upload = $input.item.json;
const url = upload.data?.url || upload.data?.display_url;

const results = [{ slide_number: 1, headline: prev.headline, subtext: prev.subtext, image_url: url }];

// Check if more slides
if (prev.remaining_slides.length === 0) {
  return [{ json: { done: true, results: results } }];
}

const next = prev.remaining_slides[0];
const prompt = `Create a 4:5 portrait Instagram carousel image.

${prev.style}

=== SLIDE 2 of ${prev.total} ===
HEADLINE: "${next.headline}"
TOPIC: ${prev.topic}

=== CRITICAL REQUIREMENTS ===
1. HEADLINE TEXT: Bold, clear "${next.headline}" text in ${prev.ac} color
2. TEXT AREA: Dark gradient (${prev.dc}) behind headline for readability
3. MSI LOGO: Place the provided logo in BOTTOM RIGHT corner - DO NOT modify
4. QUALITY: Professional Instagram-ready, high resolution
5. CONSISTENCY: MUST match slide 1 style EXACTLY - same colors, same effects, same composition style

=== DO NOT ===
- Do NOT add any text other than the headline
- Do NOT modify the MSI logo
- Do NOT change the visual style from slide 1`;

return [{ json: { done: false, slide_num: 2, headline: next.headline, subtext: next.subtext || '', prompt: prompt, logo: prev.logo, remaining_slides: prev.remaining_slides.slice(1), total: prev.total, style: prev.style, topic: prev.topic, ac: prev.ac, dc: prev.dc, results: results } }];

const prev = $('Collect 1').item.json;
const resp = $input.item.json;

let imgData = '';
const candidates = resp.candidates || [];
for (const c of candidates) {
  for (const p of (c?.content?.parts || [])) {
