  if (imgData) break;
}
if (!imgData) throw new Error('No image for slide 3');

return [{ json: { ...prev, img_data: imgData } }];

const prev = $('Extract 3').item.json;
const upload = $input.item.json;
const url = upload.data?.url || upload.data?.display_url;

const results = [...prev.results, { slide_number: 3, headline: prev.headline, subtext: prev.subtext, image_url: url }];

return [{ json: { done: true, results: results } }];

const data = $input.item.json;
const slides = data.results.sort((a, b) => a.slide_number - b.slide_number);

return [{ json: { success: true, message: `Generated ${slides.length} carousel slides`, data: { total_slides: slides.length, slides: slides } } }];
