var wh = $input.first().json;
var data = wh;
if (wh.body) {
  if (typeof wh.body === 'string') {
    try { data = JSON.parse(wh.body); } catch(e) { data = wh; }
  } else { data = wh.body; }
}
var newsItems = data.news_items || [];
if (newsItems.length === 0 || newsItems.length > 3) {
  return [{ json: { error: true, message: 'Se requieren entre 1 y 3 noticias' } }];
}
var newsSummary = newsItems.map(function(n, i) {
  return 'NOTICIA ' + (i+1) + ':\nTitulo: ' + n.title + '\nFuente: ' + (n.source || 'Desconocido') + '\nContenido: ' + (n.content || n.snippet || '').substring(0, 1500);
}).join('\n\n');
return [{ json: { news_items: newsItems, news_count: newsItems.length, news_summary: newsSummary, visual_style: data.visual_style || 'Realistic', request_id: Date.now().toString() } }];

