var aiOut = $('AI: Copywriting').first().json.output;
var newsData = $('Parse News').first().json;
var logoItem = $input.item;
var logo = '';
if (logoItem.binary) {
  var k = Object.keys(logoItem.binary)[0];
  var buf = await this.helpers.getBinaryDataBuffer(0, k);
  logo = buf.toString('base64');
}
var parsed;
try {
  var clean = aiOut.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
  var match = clean.match(/\{[\s\S]*\}/);
  if (match) clean = match[0];
  parsed = JSON.parse(clean);
} catch(e) {
  parsed = { topic: 'Technology Update', hook: 'Tech News You Need', slides: newsData.news_items.map(function(n) { return { headline: n.title.substring(0, 40) }; }), hashtags: ['TechNews', 'MSITechnologies', 'B2BTech', 'Innovation', 'DigitalTransformation'], context: '', caption: '' };
}
var removeEmoji = function(s) { return s ? s.replace(/[\u{1F600}-\u{1F64F}\u{1F300}-\u{1F5FF}\u{1F680}-\u{1F6FF}\u{2600}-\u{26FF}]/gu, '').trim() : ''; };
var t = removeEmoji(parsed.topic || 'Technology');
var hook = removeEmoji(parsed.hook || 'Your Weekly Tech Digest');
var sl = parsed.slides || [];
var h1 = t;
var h2 = sl[0] ? removeEmoji(sl[0].headline) : 'Tech Update';
var h3 = sl[1] ? removeEmoji(sl[1].headline) : 'Industry News';
var h4 = sl[2] ? removeEmoji(sl[2].headline) : 'Latest Trends';
var h5 = 'Stay Informed with MSI';
var ctx = removeEmoji(parsed.context || '');
var caption = removeEmoji(parsed.caption || '');
var hashtags = parsed.hashtags || [];
return [{ json: { topic: t, hook: hook, context: ctx, logo: logo, h1: h1, h2: h2, h3: h3, h4: h4, h5: h5, caption: caption, hashtags: hashtags, request_id: newsData.request_id } }];

var p = $('Prepare Carousel').item.json;
var u1 = $('Upload 1').item.json.data ? $('Upload 1').item.json.data.url : '';
var u2 = $('Upload 2').item.json.data ? $('Upload 2').item.json.data.url : '';
var u3 = $('Upload 3').item.json.data ? $('Upload 3').item.json.data.url : '';
var u4 = $('Upload 4').item.json.data ? $('Upload 4').item.json.data.url : '';
var u5 = $input.item.json.data ? $input.item.json.data.url : '';
var slides = [
  { slide_number: 1, headline: p.h1, image_url: u1 },
  { slide_number: 2, headline: p.h2, image_url: u2 },
  { slide_number: 3, headline: p.h3, image_url: u3 },
  { slide_number: 4, headline: p.h4, image_url: u4 },
  { slide_number: 5, headline: p.h5, image_url: u5 }
];
var urls = '';
if (u1) urls += u1;
if (u2) urls += (urls ? ',' : '') + u2;
if (u3) urls += (urls ? ',' : '') + u3;
if (u4) urls += (urls ? ',' : '') + u4;
if (u5) urls += (urls ? ',' : '') + u5;
var hashtagStr = p.hashtags ? p.hashtags.map(function(h) { return '#' + h; }).join(' ') : '';
return [{ json: { success: true, data: { total_slides: 5, slides: slides }, topic: p.topic, headline: p.h1, hook: p.hook, context: p.context, caption: p.caption, hashtags: hashtagStr, post_copy: p.caption || (p.h1 + ' | ' + p.h2 + ' | ' + p.h3 + ' | ' + p.h4 + ' | ' + p.h5), all_image_urls: urls, visual_style: 'Realistic', slide_count: 5, request_id: p.request_id } }];
