  if (typeof input.body === 'string') {
    try {
      data = JSON.parse(input.body);
      console.log('Parsed JSON body successfully');
    } catch (e) {
      console.error('Failed to parse body as JSON:', e.message);
      data = input;
    }
  } else if (typeof input.body === 'object') {
    data = input.body;
    console.log('Body is already an object');
  } else {
    data = input;
  }
} else if (input.query) {
  data = input.query;
  console.log('Using input.query');
} else {
  data = input;
  console.log('Using input directly');
}

console.log('Extracted data:', JSON.stringify(data, null, 2));

// Construir variables (soportar snake_case y camelCase)
const topic = data.topic || '';
const post_type = data.post_type || data.postType || '';
const visual_style = data.visual_style || data.visualStyle || '';
const orientation = data.orientation || '';
const headline = data.headline || '';
const data_points = data.data_points || data.dataPoints || '';
const context = data.context || '';

console.log('Final variables:', {topic, post_type, visual_style, orientation, headline, data_points, context});
console.log('==== END WEBHOOK INPUT ====');

return [{
  json: {
    topic,
    post_type,
    visual_style,
    orientation,
    headline,
    data_points,
    context
  }
}];

