// Merge prompt data with logo binary for multi-image Gemini API call
const promptData = $('Update Image Prompt in DB').item.json;
const logoItem = $('Download MSI Logo').item;

console.log('==== PREPARE MULTI-IMAGE INPUT ====');
console.log('Prompt data:', {
  id: promptData.id,
  image_prompt_length: promptData.image_prompt?.length || 0,
  orientation: promptData.orientation
});
console.log('Logo binary keys:', Object.keys(logoItem.binary || {}));

// Get logo binary data and convert to base64
let logoBase64 = '';
if (logoItem.binary && Object.keys(logoItem.binary).length > 0) {
  const binaryKey = Object.keys(logoItem.binary)[0];
  const binaryData = logoItem.binary[binaryKey];

  console.log('Binary data structure:', {
    hasData: !!binaryData.data,
    dataType: typeof binaryData.data,
    dataValue: binaryData.data,
    mimeType: binaryData.mimeType,
    id: binaryData.id
  });

  // In n8n filesystem mode, we need to use helpers to get the actual data
  try {
    // Use $binary helper to get the actual buffer content
    const buffer = await this.helpers.getBinaryDataBuffer(logoItem.json.index || 0, binaryKey);
    logoBase64 = buffer.toString('base64');
    console.log('Logo base64 extracted from buffer, length:', logoBase64.length);
    console.log('First 50 chars:', logoBase64.substring(0, 50));
  } catch (e) {
    console.error('Failed to get binary buffer:', e.message);

    // Fallback: try direct access
    if (typeof binaryData.data === 'string' && binaryData.data !== 'filesystem-v2') {
      logoBase64 = binaryData.data;
    } else if (Buffer.isBuffer(binaryData.data)) {
      logoBase64 = binaryData.data.toString('base64');
    } else if (binaryData.data instanceof Uint8Array) {
      logoBase64 = Buffer.from(binaryData.data).toString('base64');
    }
  }
} else {
  console.log('WARNING: No logo binary found');
}

if (!logoBase64 || logoBase64 === 'filesystem-v2') {
  throw new Error('Failed to extract logo base64 data. Got: ' + logoBase64);
}

console.log('==== END PREPARE MULTI-IMAGE INPUT ====');

return [{
  json: {
    image_prompt: promptData.image_prompt,
    logo_base64: logoBase64,
    post_id: promptData.id,
    orientation: promptData.orientation
  }
}];

