// Extract image binary from Gemini REST API response
const item = $input.item;

console.log('==== CHECK IMAGE BINARY ====');
console.log('Item binary keys:', Object.keys(item.binary || {}));
console.log('Item json keys:', Object.keys(item.json || {}));

// Check if binary data exists (from HTTP Request node)
if (item.binary && Object.keys(item.binary).length > 0) {
  const binaryKey = Object.keys(item.binary)[0];
  console.log('Found binary key:', binaryKey);
  console.log('Binary data size:', item.binary[binaryKey]?.data?.length || 0);

  // Rename binary to 'data' for ImgBB
  return [{
    json: item.json,
    binary: {
      data: item.binary[binaryKey]
    }
  }];
}

// Check if response has inline_data or inlineData (Gemini API structure - support both formats)
const parts = item.json?.candidates?.[0]?.content?.parts?.[0];
const inlineData = parts?.inline_data || parts?.inlineData;

if (inlineData) {
  console.log('Found inline image data from Gemini API');
  console.log('Mime type:', inlineData.mime_type || inlineData.mimeType);
  console.log('Data size:', inlineData.data?.length || 0);

  // Convert base64 to binary for ImgBB
  return [{
    json: item.json,
    binary: {
      data: {
        data: inlineData.data,
        mimeType: inlineData.mime_type || inlineData.mimeType || 'image/png',
        fileName: 'generated-image.png'
      }
    }
  }];
}

