// Prepara imágenes para ImgBB
// Soporta tanto binarios nativos de n8n como base64 desde JSON (una o múltiples)
const items = $input.all();
const newItems = [];

console.log('==== SEPARAR BINARIOS START ====');
console.log('SEPARAR BINARIOS - Total items recibidos:', items.length);

for (const item of items) {
  console.log('\n--- SEPARAR BINARIOS - Procesando item ---');
  console.log('SEPARAR BINARIOS - Item.json keys:', Object.keys(item.json));
  console.log('SEPARAR BINARIOS - Tiene binary?:', !!item.binary);
  if (item.binary) {
    console.log('SEPARAR BINARIOS - Binary keys:', Object.keys(item.binary));
  }

  // IMPORTANTE: Si body es un string JSON, parsearlo
  let webhookData = {};
  let parsedData = {};

  if (item.json.body && typeof item.json.body === 'string') {
    try {
      parsedData = JSON.parse(item.json.body);
      console.log('SEPARAR BINARIOS - Body parseado:', JSON.stringify(parsedData));
      webhookData = parsedData;
    } catch (e) {
      console.log('SEPARAR BINARIOS - Error parseando body:', e.message);
      webhookData = item.json;
    }
  } else {
    webhookData = item.json;
  }

  console.log('SEPARAR BINARIOS - webhookData final:', JSON.stringify(webhookData));

  // Caso 1: Ya tiene binarios (desde n8n)
  const binaryData = item.binary;
  if (binaryData) {
    for (const key of Object.keys(binaryData)) {
      if (key.startsWith('Image') || key === 'data') {
        const resultItem = {
          json: webhookData,
          binary: {
            data: binaryData[key]
          }
        };
        console.log('SEPARAR BINARIOS - Retornando con binario:', JSON.stringify(resultItem.json));
        newItems.push(resultItem);
      }
    }
    continue;
  }

  // Caso 2: Múltiples imágenes (Images array)
  const imagesArray = webhookData.Images;
  if (imagesArray && Array.isArray(imagesArray) && imagesArray.length > 0) {
    console.log('SEPARAR BINARIOS - Formato detectado: Images array con', imagesArray.length, 'imágenes');

    for (let i = 0; i < imagesArray.length; i++) {
      const imageData = imagesArray[i];
      try {
        const base64Data = imageData.data.split(',')[1] || imageData.data;
        const buffer = Buffer.from(base64Data, 'base64');

        newItems.push({
          json: webhookData,
          binary: {
            data: {
              data: buffer.toString('base64'),
              mimeType: imageData.mimeType || 'image/png',
              fileName: imageData.fileName || `image_${i}.png`,
              fileExtension: (imageData.fileName || 'image.png').split('.').pop()
            }
          }
        });
        console.log('SEPARAR BINARIOS - Imagen', i + 1, 'procesada exitosamente');
        console.log('SEPARAR BINARIOS - Binary data length:', buffer.length);
      } catch (error) {
        console.log('SEPARAR BINARIOS - ERROR procesando imagen', i, ':', error.message);
        console.log('SEPARAR BINARIOS - imageData:', JSON.stringify(imageData));
        // NO agregar items sin binario - dejar que falle para detectar el problema
      }
    }
    console.log('SEPARAR BINARIOS - Images array completado. Items creados:', newItems.length);
    continue;
  }

  // Caso 3: Una sola imagen (Image objeto)
  const imageData = webhookData.Image;
  let hasImage = false;
  let imageToProcess = null;

  // Formato 1: Image.data (objeto con data)
  if (imageData && imageData.data) {
    console.log('SEPARAR BINARIOS - Formato detectado: Image.data');
    hasImage = true;
    imageToProcess = imageData;
  }
  // Formato 2: Image es directamente el string base64
  else if (typeof imageData === 'string' && imageData.length > 100) {
    console.log('SEPARAR BINARIOS - Formato detectado: Image como string base64');
    hasImage = true;
    imageToProcess = {
      data: imageData,
      mimeType: 'image/png',
      fileName: 'image.png'
    };
  }
  // Formato 3: Imagen en otro campo
  else if (webhookData.image && typeof webhookData.image === 'string' && webhookData.image.length > 100) {
    console.log('SEPARAR BINARIOS - Formato detectado: campo image (minúscula)');
    hasImage = true;
    imageToProcess = {
      data: webhookData.image,
      mimeType: 'image/png',
      fileName: 'image.png'
    };
  }

  if (hasImage && imageToProcess) {
    try {
      const base64Data = imageToProcess.data.split(',')[1] || imageToProcess.data;
      const buffer = Buffer.from(base64Data, 'base64');

      const resultItem = {
        json: webhookData,
        binary: {
          data: {
            data: buffer.toString('base64'),
            mimeType: imageToProcess.mimeType || 'image/png',
            fileName: imageToProcess.fileName || 'image.png',
            fileExtension: (imageToProcess.fileName || 'image.png').split('.').pop()
          }
        }
      };
      console.log('SEPARAR BINARIOS - Retornando con imagen base64');
      newItems.push(resultItem);
    } catch (error) {
      console.log('SEPARAR BINARIOS - Error procesando imagen:', error.message);
      newItems.push({
        json: webhookData
      });
    }
  } else {
    // Caso 4: No hay imagen
    console.log('SEPARAR BINARIOS - Sin imagen, pasando datos originales');
    newItems.push({
      json: webhookData
    });
  }
}

if (newItems.length === 0 && items.length > 0) {
  console.log('SEPARAR BINARIOS - WARNING: No hay items procesados, devolviendo originales');
  return items;
}

console.log('==== SEPARAR BINARIOS END ====');
console.log('SEPARAR BINARIOS - Total items devueltos:', newItems.length);
for (let i = 0; i < newItems.length; i++) {
  console.log(`SEPARAR BINARIOS - Item ${i}: tiene binary.data =`, !!newItems[i].binary?.data);
}

return newItems;

// AGRUPA TODOS LOS RESULTADOS DE IMGBB EN UN SOLO ITEM
const allItems = $input.all();

console.log('==== AGGREGATE IMGBB START ====');
console.log('AGGREGATE - Total items recibidos:', allItems.length);

// OBTENER DATOS ORIGINALES DEL WEBHOOK desde 'Check if Image Exists'
let baseData = {};
const checkImageNode = $('Check if Image Exists');
if (checkImageNode && checkImageNode.first) {
  const firstCheckItem = checkImageNode.first();
  if (firstCheckItem && firstCheckItem.json) {
    baseData = { ...firstCheckItem.json };
    console.log('AGGREGATE - Datos base desde Check if Image Exists');
    console.log('AGGREGATE - post_type:', baseData.post_type);
    console.log('AGGREGATE - publish_linkedin:', baseData.publish_linkedin);
    console.log('AGGREGATE - publish_facebook:', baseData.publish_facebook);
  }
} else {
  console.log('AGGREGATE - WARNING: No se pudo acceder a Check if Image Exists, usando primer item de ImgBB');
  baseData = allItems[0]?.json || {};
}

// Crear array con todas las URLs de ImgBB
const imageArray = [];
for (const item of allItems) {
  if (item.json.data && item.json.data.url) {
    imageArray.push({
      url: item.json.data.url,
      display_url: item.json.data.display_url || item.json.data.url,
      delete_url: item.json.data.delete_url,
      filename: item.json.data.image?.filename || 'image.jpg',
      type: item.json.data.image?.mime || 'image/jpeg'
    });
  }
}

console.log('AGGREGATE - Total imágenes en array:', imageArray.length);

// Resultado: UN solo item con DATOS ORIGINALES DEL WEBHOOK + todas las imágenes
const result = {
  ...baseData,
  Image: imageArray,
  image_count: imageArray.length
};

console.log('AGGREGATE - post_type en resultado:', result.post_type);
console.log('AGGREGATE - Array final con', imageArray.length, 'URLs');
console.log('==== AGGREGATE IMGBB END ====');

return [{
  json: result
}];

// PREPARAR DATOS PARA GUARDAR EN BD
// Este nodo convierte el array Image[] en un string JSON para el campo image_url

const item = $input.item.json;

console.log('==== PREPARE DB DATA START ====');
console.log('PREPARE DB - Input item.Image type:', Array.isArray(item.Image) ? 'array' : typeof item.Image);
console.log('PREPARE DB - Input item.Image length:', Array.isArray(item.Image) ? item.Image.length : 'N/A');
console.log('PREPARE DB - post_id exists?', !!item.post_id);

let imageUrlString = null;

if (item.Image) {
  if (Array.isArray(item.Image)) {
    // Es un array, extraer solo las URLs y convertir a JSON string
    const urls = item.Image.map(img => img.url);
    imageUrlString = JSON.stringify(urls);
    console.log('PREPARE DB - Created URL array:', imageUrlString);
  } else if (typeof item.Image === 'object' && item.Image.url) {
    // Es un solo objeto, convertir a array de un elemento
    imageUrlString = JSON.stringify([item.Image.url]);
    console.log('PREPARE DB - Created single URL array:', imageUrlString);
  } else {
    console.log('PREPARE DB - WARNING: Image tiene formato inesperado:', JSON.stringify(item.Image));
  }
} else {
  console.log('PREPARE DB - No Image field found, image_url will be null');
}

// Crear resultado con campos preparados para UPSERT automático
// Solo incluimos los campos que queremos actualizar/insertar
const result = {
  id: item.post_id || null,
  post_type: item.post_type,
  image_url: imageUrlString,
  status: item.Image ? 'completed' : 'pending',
  publish_linkedin: item.publish_linkedin || 'No',
  publish_facebook: item.publish_facebook || 'No',
  publish_instagram: item.publish_instagram || 'No'
};


if (result.id) {  console.log('PREPARE DB - post_id present, will UPSERT:', result.id);} else {  console.log('PREPARE DB - No post_id (id is null), will INSERT new record');}

console.log('PREPARE DB - Final image_url:', imageUrlString);
console.log('PREPARE DB - Final result:', JSON.stringify(result));
console.log('==== PREPARE DB DATA END ====');

return [{json: result}];

// PREPARA LINKEDIN: Descarga imágenes desde URLs de ImgBB
const currentItem = $input.first();
const baseJson = currentItem.json;

console.log('==== PREP LI START ====');
console.log('PREP LI - Image array LENGTH:', baseJson.Image ? baseJson.Image.length : 0);

// Descargar imágenes desde URLs de ImgBB
if (baseJson.Image && Array.isArray(baseJson.Image) && baseJson.Image.length > 0) {
  console.log('PREP LI - Descargando', baseJson.Image.length, 'imágenes desde URLs...');

  const binaryData = {};
  const downloadedCount = [];

  for (let i = 0; i < baseJson.Image.length; i++) {
    const imgData = baseJson.Image[i];
    if (!imgData.url) {
      console.log('PREP LI - ERROR: Imagen', i, 'no tiene URL');
      continue;
    }

    try {
      console.log(`PREP LI - Descargando imagen ${i + 1}/${baseJson.Image.length}:`, imgData.url);

      const response = await this.helpers.request({
        method: 'GET',
        uri: imgData.url,
        encoding: null,
        resolveWithFullResponse: false
      });

      const keyName = i === 0 ? 'Image' : `Image${i}`;
      binaryData[keyName] = {
        data: Buffer.from(response).toString('base64'),
        mimeType: imgData.type || 'image/png',
        fileName: imgData.filename || `image_${i}.png`,
        fileExtension: (imgData.filename || 'image.png').split('.').pop()
      };

      downloadedCount.push(keyName);
      console.log('PREP LI - ✓ Descargada:', keyName, '- size:', response.length, 'bytes');
    } catch (error) {
      console.log('PREP LI - ERROR descargando imagen', i, ':', error.message);
    }
  }

  if (downloadedCount.length > 0) {
    console.log('PREP LI - Total descargadas:', downloadedCount.length);
    console.log('PREP LI - Binary keys creadas:', downloadedCount.join(', '));
    console.log('PREP LI - ROUTER EVALUARÁ:', baseJson.Image.length);
    console.log('==== PREP LI END (OK) ====');
    return [{
      json: baseJson,
      binary: binaryData
    }];
  } else {
    console.log('PREP LI - ERROR: No se pudo descargar ninguna imagen');
  }
}

console.log('PREP LI - Sin imágenes o error');
console.log('==== PREP LI END ====');
return [{ json: baseJson }];

// SEPARA MÚLTIPLES IMÁGENES PARA LINKEDIN
const allItems = $input.all();
const newItems = [];

console.log('==== SEPARATE IMAGES LI START ====');
console.log('SEPARATE LI - Total items recibidos:', allItems.length);

// DEBUG: Ver qué binarios vienen en el primer item
if (allItems.length > 0 && allItems[0].binary) {
  console.log('SEPARATE LI - Binary keys en item 0:', Object.keys(allItems[0].binary));
  console.log('SEPARATE LI - Image array en json:', allItems[0].json.Image ? allItems[0].json.Image.length : 0);
}

// Obtener binarios del item actual (vienen de Prep URL LI)
for (let idx = 0; idx < allItems.length; idx++) {
  const item = allItems[idx];
  console.log(`\nSEPARATE LI - Item ${idx}:`);
  console.log('SEPARATE LI - Tiene binary?:', !!item.binary);

  const binaryData = item.binary;

  if (!binaryData) {
    console.log('SEPARATE LI - ERROR: No hay binarios en el item');
    console.log('SEPARATE LI - item.json keys:', Object.keys(item.json));
    continue;
  }

  console.log('SEPARATE LI - Binary keys:', Object.keys(binaryData));

  // Recorrer todas las llaves del objeto binario (Image, Image1, Image2, etc.)
  for (const key of Object.keys(binaryData)) {
    if (key.startsWith('Image') || key === 'data') {
      const newItem = {
        json: {
          ...item.json,
          filename: binaryData[key].fileName,
          mimetype: binaryData[key].mimeType
        },
        binary: {
          data: binaryData[key]
        }
      };
      newItems.push(newItem);
      console.log('SEPARATE LI - Binario separado:', key, '- tiene data?:', !!newItem.binary.data);
    }
  }
}

console.log('\nSEPARATE LI - Total items devueltos:', newItems.length);
for (let i = 0; i < newItems.length; i++) {
  console.log(`SEPARATE LI - Output item ${i}: tiene binary.data =`, !!newItems[i].binary?.data);
}
console.log('==== SEPARATE IMAGES LI END ====');
return newItems;

// Obtenemos todos los ítems del nodo de registro
const itemsRegistro = $('Register Load').all();

const mediaItems = [];

// Recorremos cada respuesta de LinkedIn
for (const item of itemsRegistro) {
    // Obtenemos el URN (el identificador de la imagen)
    const urn = item.json.value.image;

    if (urn) {
        // CORRECCIÓN: LinkedIn exige que la llave se llame "id", no "media"
        mediaItems.push({
            "id": urn
        });
    }
}

// Recuperamos el post_type del nodo Merge Webhook with DB
const postText = $('Merge Webhook with DB').first().json.post_type;

// Devolvemos la lista limpia y correcta CON el post_type
return [{
    json: {
        contentEntities: mediaItems,
        post_type: postText
    }
}];

// PREPARA FB: Asegura que Image array esté disponible
const currentItem = $input.first();
const baseJson = currentItem.json;

console.log('==== PREP FB START ====');
console.log('PREP FB - RECIBIENDO DATOS');
console.log('PREP FB - baseJson.Image exists:', !!baseJson.Image);
console.log('PREP FB - baseJson.Image is array:', Array.isArray(baseJson.Image));
console.log('PREP FB - Image array LENGTH:', baseJson.Image ? baseJson.Image.length : 'UNDEFINED');
if (baseJson.Image && Array.isArray(baseJson.Image)) {
  console.log('PREP FB - Image URLs:', baseJson.Image.map(img => img.url));
}
console.log('PREP FB - image_url de BD:', baseJson.image_url);

// Si ya tiene Image array (desde Merge Webhook with DB), usarlo
if (baseJson.Image && Array.isArray(baseJson.Image) && baseJson.Image.length > 0) {
  console.log('PREP FB - Usando Image array existente:', baseJson.Image.length);
  console.log('PREP FB - ROUTER EVALUARÁ:', baseJson.Image && Array.isArray(baseJson.Image) ? baseJson.Image.length : 0);
  console.log('==== PREP FB END (con Image[]) ====');
  return [{
    json: baseJson
  }];
}

// Si no tiene Image array pero tiene image_url, crearlo
if (baseJson.image_url) {
  console.log('PREP FB - Creando Image array desde image_url');
  baseJson.Image = [{
    url: baseJson.image_url,
    filename: 'fb_image.jpg'
  }];
  console.log('==== PREP FB END (creado desde URL) ====');
  return [{
    json: baseJson
  }];
}

console.log('PREP FB - No hay imágenes');
console.log('==== PREP FB END (texto) ====');
return [{
  json: baseJson
}];

// SEPARA ITEMS PARA FB MULTIPLE (usando URLs)
const items = $input.all();
const imageArray = items[0].json.Image;
const newItems = [];

for (const img of imageArray) {
   newItems.push({
     json: {
       ...items[0].json,
       url_to_upload: img.url
     }
   });
}

return newItems;

const itemsUploaded = $('FB Upload URL (Hidden)').all();

// Obtener post_type del primer item de FB Upload URL (Hidden)
// que debería tener los datos parseados
const originalPost = itemsUploaded[0]?.json?.post_type || $input.first().json.post_type;

const mediaItems = [];

// Recolectamos los IDs que nos devolvió Facebook
for (const item of itemsUploaded) {
    const mediaId = item.json.id;
    if (mediaId) {
        mediaItems.push({
            "media_fbid": mediaId
        });
    }
}

return [{
    json: {
        attached_media: mediaItems,
        post_type: originalPost
    }
}];

// Extrae las URLs del array Image[] para Instagram
const items = $input.all();
const firstItem = items[0]?.json || {};

// Recuperamos el caption
const caption = firstItem.post_type || '';

// Extraemos las URLs del array Image[]
const images = [];
if (firstItem.Image && Array.isArray(firstItem.Image)) {
  for (const img of firstItem.Image) {
    if (img.url) {
      images.push(img.url);
    }
  }
}

console.log('AGRUPAR IMGBB - Total imágenes:', images.length);

// Devolvemos UN solo ítem con la lista y el conteo
return [{
  json: {
    images_list: images,
    total_count: images.length,
    caption: caption
  }
}];

// Separamos la lista de nuevo para crear los ítems del carrusel uno por uno
const images = $input.first().json.images_list;
const caption = $input.first().json.caption;

return images.map(url => ({
  json: {
    url: url,
    parent_caption: caption
  }
}));

// Agrupamos los IDs generados para crear el contenedor padre
const items = $input.all();
const ids = items.map(i => i.json.id);
// El caption viene arrastrado del nodo anterior
const caption = items[0].json.parent_caption || items[0].json.caption || '';

return [{
  json: {
    children_ids: ids,
    caption: caption
  }
}];

// Combinar datos del webhook con la respuesta de la BD
// ESTE NODO PRESERVA EL ARRAY Image[] DESDE Merge ImgBB Response
const dbResponse = $input.item.json;

console.log('==== MERGE DB START ====');

// Obtener datos COMPLETOS desde Merge ImgBB Response (que ya tiene Image[] agregado)
const mergeImgBBNode = $('Merge ImgBB Response');
let webhookData = {};
let imageArray = [];

if (mergeImgBBNode && mergeImgBBNode.item && mergeImgBBNode.item.json) {
  // Tomar TODOS los datos de Merge ImgBB Response (que ya tiene Image[] completo)
  webhookData = { ...mergeImgBBNode.item.json };
  imageArray = webhookData.Image || [];
  console.log('MERGE DB - Datos desde Merge ImgBB Response');
  console.log('MERGE DB - Image array recibido:', Array.isArray(imageArray) ? imageArray.length : 0);
  if (Array.isArray(imageArray) && imageArray.length > 0) {
    console.log('MERGE DB - Image[0].url:', imageArray[0].url);
    if (imageArray.length > 1) {
      console.log('MERGE DB - Image[1].url:', imageArray[1].url);
    }
  }
} else {
  console.log('MERGE DB - ERROR: No se encontró Merge ImgBB Response');
  const checkImageNode = $('Check if Image Exists');
  if (checkImageNode && checkImageNode.item) {
    webhookData = checkImageNode.item.json;
  }
}

// Validar que el array esté presente
if (!Array.isArray(imageArray)) {
  imageArray = [];
  console.log('MERGE DB - WARNING: Image no es array, inicializando vacío');
}

// Resultado final - mantener Image[] exactamente como viene de Merge ImgBB Response
const result = {
  ...webhookData,
  db_record: dbResponse,
  image_url: dbResponse.image_url,
  // NO sobrescribir Image[], mantener el array completo de Merge ImgBB Response
  Image: imageArray
};

console.log('MERGE DB - Image array FINAL LENGTH:', result.Image ? result.Image.length : 0);
console.log('MERGE DB - result.Image content:', JSON.stringify(result.Image));
console.log('==== MERGE DB END ====');

return [{
  json: result
}];
