// Format Input for Educational Content
const input = $('Webhook - Educative Carousel').item.json;
let data = input.body || input;
if (typeof data === 'string') {
  try { data = JSON.parse(data); } catch(e) { data = input; }
}

// Educational content structure - NO NEWS ELEMENTS
const topic = data.topic || 'Business Process Automation';
const pillar = data.pillar || 'Educational Content & Technology Learning';
const theme = data.theme || 'Professional Development & Skills';
const context = data.context || '';

return [{
  json: {
    topic: topic,
    pillar: pillar,
    theme: theme,
    context: context,
    visual_style: 'Realistic Photography',
    content_type: 'Educational Carousel',
    color_primary: data.color_primary || '#207CE5',
    color_secondary: data.color_secondary || '#004AAD'
  }
}];

// Parse Final JSON Output
const rawOutput = $('Agent 3: Visual Director').item.json.output;
const cleanedOutput = rawOutput.replace(/```json/g, '').replace(/```/g, '').trim();
const generated = JSON.parse(cleanedOutput);
const originalInput = $('Format Input').item.json;

// Add metadata to each slide
const slides = generated.map(slide => ({
    ...slide,
    visual_style: originalInput.visual_style,
    carousel_id: null
}));

return [{
    json: {
        slides: slides,
        parent_post: {
            topic: originalInput.topic,
            pillar: originalInput.pillar,
            theme: originalInput.theme,
            headline: slides[0].headline,
            visual_style: originalInput.visual_style,
            post_type: 'Educative Carousel',
            status: 'generating_images'
        }
    }
}];

// Prepare All Slides Data
const postId = $('Create Post Record').item.json.id;
const slides = $('Parse Output').item.json.slides;
const topic = $('Format Input').item.json.topic || 'Technology';

// Return all slides as individual items for batch insert
// Embed topic inside visual_style JSON since carousel_slides table doesn't have topic column
const slideItems = slides.map(slide => ({
    json: {
        carousel_id: postId,
        slide_number: slide.slide_number,
        headline: slide.headline,
        subtext: slide.subtext || '',
        body_text: slide.body || slide.body_text || '',
        image_prompt: slide.image_prompt,
        visual_style: JSON.stringify({ style: slide.visual_style, topic: topic })
    }
}));

return slideItems;

// Get all inserted slides and prepare for sequential processing
const allInserted = $input.all().map(item => item.json);

// Extract topic from visual_style JSON
let topic = 'Technology';
try {
  const vsData = JSON.parse(allInserted[0].visual_style || '{}');
  topic = vsData.topic || 'Technology';
} catch(e) {}

// Store all slides data for the chain
const s1 = allInserted[0];
const s2 = allInserted[1];
const s3 = allInserted[2];
const s4 = allInserted[3];
const s5 = allInserted[4];

return [{
  json: {
    // Current slide to process
    id: s1.id,
    carousel_id: s1.carousel_id,
    slide_number: s1.slide_number,
    headline: s1.headline || '',
    subtext: s1.subtext || '',
    body_text: s1.body_text || '',
    image_prompt: s1.image_prompt || '',
    visual_style: s1.visual_style || '',
    topic: topic,
    // All slides for the chain
    s2_id: s2.id, s2_headline: s2.headline || '', s2_subtext: s2.subtext || '', s2_body: s2.body_text || '', s2_prompt: s2.image_prompt || '', s2_style: s2.visual_style || '',
    s3_id: s3.id, s3_headline: s3.headline || '', s3_subtext: s3.subtext || '', s3_body: s3.body_text || '', s3_prompt: s3.image_prompt || '', s3_style: s3.visual_style || '',
    s4_id: s4.id, s4_headline: s4.headline || '', s4_subtext: s4.subtext || '', s4_body: s4.body_text || '', s4_prompt: s4.image_prompt || '', s4_style: s4.visual_style || '',
    s5_id: s5.id, s5_headline: s5.headline || '', s5_subtext: s5.subtext || '', s5_body: s5.body_text || '', s5_prompt: s5.image_prompt || '', s5_style: s5.visual_style || '',
    carousel_id_shared: s1.carousel_id
  }
}];

const input = $input.first().json;
const response = await this.helpers.httpRequest({
  method: 'POST',
  url: 'https://n8nmsi.app.n8n.cloud/webhook/msi-educative-slide-gen',
  body: {
    slide_id: input.id,
    image_prompt: input.image_prompt,
    carousel_id: input.carousel_id,
    slide_number: input.slide_number,
    headline: input.headline,
    subtext: input.subtext,
    body_text: input.body_text,
    visual_style: input.visual_style,
    topic: input.topic
  },
  json: true
});

// Pass all slide data to next node
return [{ json: { ...response, ...input } }];

const prev = $input.first().json;

return [{
  json: {
