// Prepare Gemini Input - Build complete EDUCATIVE slide image prompt
const inputData = $('Webhook - Educative Slide Gen').item.json;
const input = inputData.body || inputData;
const logoItem = $('Download MSI Logo').item;

// Get logo base64
let logoBase64 = '';
if (logoItem.binary && Object.keys(logoItem.binary).length > 0) {
  const binaryKey = Object.keys(logoItem.binary)[0];
  const binaryData = logoItem.binary[binaryKey];

  try {
    const buffer = await this.helpers.getBinaryDataBuffer(0, binaryKey);
    logoBase64 = buffer.toString('base64');
  } catch (e) {
     if (binaryData.data && binaryData.data !== 'filesystem-v2') {
         logoBase64 = binaryData.data;
     }
  }
}

// Extract all slide content fields
const slideNumber = parseInt(input.slide_number) || 1;
const headline = input.headline || '';
const subtext = input.subtext || '';
const bodyText = input.body_text || '';
const topic = input.topic || 'Professional Development';

// NO SOURCE for educational content
let educationalContext = 'Educational Content';
try {
  if (input.visual_style) {
    const vs = typeof input.visual_style === 'string' ? JSON.parse(input.visual_style) : input.visual_style;
    educationalContext = vs.topic || 'Educational Content';
  }
} catch (e) {
  educationalContext = 'Educational Content';
}

// Generate EDUCATIONAL tagline for Slide 1 based on topic
const topicLower = topic.toLowerCase();
let educationalTagline = 'Learn & Grow with MSI';
if (topicLower.includes('ai') || topicLower.includes('artificial intelligence') || topicLower.includes('machine learning')) {
  educationalTagline = 'AI Education & Insights';
} else if (topicLower.includes('automation') || topicLower.includes('process')) {
  educationalTagline = 'Automation Learning Hub';
} else if (topicLower.includes('cloud') || topicLower.includes('technology')) {
  educationalTagline = 'Technology Education';
} else if (topicLower.includes('business') || topicLower.includes('efficiency')) {
  educationalTagline = 'Business Skills Development';
} else if (topicLower.includes('digital') || topicLower.includes('transformation')) {
  educationalTagline = 'Digital Transformation Learning';
} else if (topicLower.includes('data') || topicLower.includes('analytics')) {
  educationalTagline = 'Data & Analytics Education';
} else if (topicLower.includes('security') || topicLower.includes('cyber')) {
  educationalTagline = 'Cybersecurity Training';
} else if (topicLower.includes('leadership') || topicLower.includes('management')) {
  educationalTagline = 'Leadership Development';
}

const isBlueSlide = (slideNumber === 1 || slideNumber === 3 || slideNumber === 5);
const paginationNumber = slideNumber;

let fullPrompt = '';

if (slideNumber === 1) {
  fullPrompt = `Create a 4:5 Instagram carousel SLIDE 1 for MSI Technologies EDUCATIONAL content.

LOGO (CRITICAL - DO NOT MODIFY):
- Place the attached MSI logo EXACTLY as provided in TOP-LEFT corner
- Logo size: approximately 60-80 pixels height, proportional width
- DO NOT resize, redraw, recreate, or modify the logo in ANY way
- Use the EXACT logo file provided - no alterations

LAYOUT:
- ENTIRE SLIDE: Gradient background from MSI Blue #207CE5 to Dark Blue #004AAD
- MAIN VISUAL: A striking REALISTIC PHOTOGRAPH related to "${topic}" - use real-world professional scenes, modern technology in action, clean workspaces, or professional environments that directly represent the educational topic.
- The photo should have a blue color grading to blend with the gradient background.
- Style: Professional realistic photography, high quality, editorial feel

FORBIDDEN VISUAL ELEMENTS:
- NO servers or server racks
- NO cloud icons or cloud shapes
- NO network connection lines or node diagrams
- NO circuit boards or chip patterns
- NO binary code (0s and 1s)
- NO globe with connections
- NO gears or cogs
- NO illustrations or cartoons
- NO news/media elements

TYPOGRAPHY (MANDATORY):
- HEADINGS: Use ITC Avant Garde font (bold, geometric sans-serif)
- BODY/CONTENT: Use Quicksand font (rounded, friendly sans-serif)
- ALL text must have NORMAL proportions - NOT stretched, NOT condensed, NOT elongated

TEXT CONTENT (white color):
- VERY LARGE bold title in ITC Avant Garde: "${headline}"
- Smaller tagline below title in Quicksand: "${educationalTagline}"
- Bottom-RIGHT corner (visible): "Learn More >>>" in Quicksand

FORBIDDEN: News elements, stretched text, condensed fonts, illustrations, cartoons, generic stock imagery.`;

} else if (slideNumber === 5) {
  fullPrompt = `Create a 4:5 Instagram carousel SLIDE 5 - Educational CTA for MSI Technologies.

LOGO (CRITICAL - DO NOT MODIFY):
- Place the attached MSI logo EXACTLY as provided in TOP-LEFT corner
- Logo size: approximately 60-80 pixels height, proportional width
- DO NOT resize, redraw, recreate, or modify the logo in ANY way

LAYOUT:
- ENTIRE SLIDE: Gradient background from MSI Blue #207CE5 to Dark Blue #004AAD
- NO photo - solid gradient only
- Content vertically centered

TYPOGRAPHY (MANDATORY):
- HEADINGS: Use ITC Avant Garde font (bold, geometric sans-serif)
- BODY/CONTENT: Use Quicksand font (rounded, friendly sans-serif)
- ALL text must have NORMAL proportions - NOT stretched, NOT condensed, NOT elongated

TEXT CONTENT (white, centered):
- HEADING 1 (VERY LARGE, bold, ITC Avant Garde): "${headline}"
- HEADING 2 (Medium size, below heading 1, Quicksand): "${subtext || "Let's Learn Together"}"

CONTACT INFO (bottom center, LARGE white text in Quicksand):
- Line 1: envelope icon then contact@msitechnologiesinc.com
- Line 2: globe icon then msitechnologiesinc.com

FORBIDDEN: News elements, stretched text, condensed fonts, @ symbols, pagination, modifying the logo.`;

} else if (slideNumber === 2 || slideNumber === 4) {
  fullPrompt = `Create a 4:5 Instagram carousel SLIDE ${slideNumber} for MSI Technologies EDUCATIONAL content.

LOGO (CRITICAL - DO NOT MODIFY):
- Place the attached MSI logo (blue version) EXACTLY as provided in TOP-LEFT corner, overlaying the photo
- Logo size: approximately 60-80 pixels height, proportional width
- DO NOT resize, redraw, recreate, or modify the logo in ANY way

CRITICAL LAYOUT:
- The photo MUST start at the VERY TOP of the slide (y=0, pixel 0). NO white bar above.
- Top 55%: REALISTIC PHOTOGRAPH related to "${headline}". AVOID PEOPLE.
- Apply subtle BLUE GRADIENT at bottom edge of photo blending into white.
- Bottom 45%: Clean WHITE background for text.

REALISTIC PHOTOGRAPHY REQUIREMENTS:
- Professional, high-quality realistic photo
- Modern workspaces, real technology, professional settings
- Clean, editorial photography feel
- Related to the educational topic

FORBIDDEN:
- NO servers, NO cloud icons, NO circuit boards, NO binary code
- NO news/media elements, NO illustrations, NO infographics

TYPOGRAPHY (MANDATORY):
- HEADINGS: ITC Avant Garde (bold, geometric sans-serif)
- BODY: Quicksand (rounded, friendly sans-serif)
- Body text must be SHORT (max 3 lines)

TEXT (on white section, left-aligned):
- Headline in ITC Avant Garde (blue #207CE5): "${headline}"
- Body text in Quicksand (dark gray, SMALL font, max 3 lines): "${bodyText}"
- Bottom-right in Quicksand: "${paginationNumber}/5"

NO Source field. Photo starts at absolute top. DO NOT modify logo.`;

} else {
  fullPrompt = `Create a 4:5 Instagram carousel SLIDE ${slideNumber} for MSI Technologies EDUCATIONAL content.

LOGO (CRITICAL - DO NOT MODIFY):
- Place the attached MSI logo EXACTLY as provided in TOP-LEFT corner
- Logo size: approximately 60-80 pixels height, proportional width
- DO NOT resize, redraw, recreate, or modify the logo in ANY way

LAYOUT:
- Top 55%: REALISTIC PHOTOGRAPH related to "${headline}". AVOID PEOPLE. Apply BLUE GRADIENT overlay at bottom blending into blue section.
- Bottom 45%: Solid MSI Blue #207CE5

REALISTIC PHOTOGRAPHY REQUIREMENTS:
- Professional, high-quality realistic photo
- Different angle or perspective than other slides
- Clean, editorial photography feel

FORBIDDEN:
- NO servers, NO cloud icons, NO circuit boards, NO binary code
- NO news/media elements, NO illustrations

TYPOGRAPHY (MANDATORY):
- HEADINGS: ITC Avant Garde (bold, geometric sans-serif)
- BODY: Quicksand (rounded, friendly sans-serif)
- Body text must be SHORT (max 3 lines)

TEXT (on blue section, left-aligned):
- Headline in ITC Avant Garde (white): "${headline}"
- Body text in Quicksand (cream #FFFDF1, SMALL font, max 3 lines): "${bodyText}"
- Bottom-right in Quicksand: "${paginationNumber}/5"

NO Source field. AVOID showing people. REALISTIC PHOTOGRAPHY only.`;
}

return [{
  json: {
    image_prompt: fullPrompt,
    slide_id: input.slide_id,
    carousel_id: input.carousel_id,
    slide_number: slideNumber,
    headline: headline,
    subtext: subtext,
    body_text: bodyText,
    educational_context: educationalContext,
    topic: topic,
    educational_tagline: educationalTagline,
    is_blue_slide: isBlueSlide,
    logo_base64: logoBase64
  }
}];

