parameters.jsCode:
// Extract image binary from Gemini response
const item = $input.item;
const preparedData = $('Prepare Gemini Input').item.json;
const candidates = item.json?.candidates || [];

let imageData = null;
let mimeType = 'image/png';

// Search through all parts for image data
for (const candidate of candidates) {
  const parts = candidate?.content?.parts || [];
  for (const part of parts) {
    const inlineData = part?.inline_data || part?.inlineData;
    if (inlineData && inlineData.data) {
      imageData = inlineData.data;
      mimeType = inlineData.mime_type || inlineData.mimeType || 'image/png';
      break;
    }
  }
  if (imageData) break;
}

if (imageData) {
  return [{
    json: {
      slide_id: preparedData.slide_id,
      carousel_id: preparedData.carousel_id,
      slide_number: preparedData.slide_number
    },
    binary: {
      data: {
        data: imageData,
        mimeType: mimeType,
        fileName: `slide_${preparedData.slide_number}.png`
      }
    }
  }];
}

throw new Error('No image generated. Response: ' + JSON.stringify(item.json).substring(0, 500));
