// Parse incoming news data from web platform
const webhookData = $input.first().json;

let data = webhookData;
if (webhookData.body) {
  if (typeof webhookData.body === 'string') {
    try { data = JSON.parse(webhookData.body); } catch(e) { data = webhookData; }
  } else {
    data = webhookData.body;
  }
}

const newsItems = data.news_items || [];
if (newsItems.length === 0 || newsItems.length > 3) {
  throw new Error('Se requieren entre 1 y 3 noticias para generar el carrusel');
}

// MSI Brand Colors (fixed)
const brandColors = {
  primary: '#207CE5',
  secondary: '#004AAD',
  accent: '#FFFDF1',
  dark: '#2B2B2B'
};

// Visual style from request or default
const visualStyle = data.visual_style || 'Glassmorphism';

// Prepare news content summary for AI
const newsSummary = newsItems.map((n, i) => {
  return `NOTICIA ${i+1}:\nTítulo: ${n.title}\nFuente: ${n.source || 'Desconocido'}\nContenido: ${(n.content || n.snippet || '').substring(0, 1500)}\n---`;
}).join('\n\n');

return [{
  json: {
    news_items: newsItems,
    news_count: newsItems.length,
    news_summary: newsSummary,
    visual_style: visualStyle,
    brand_colors: brandColors,
    request_id: Date.now().toString()
  }
}];

// Parse AI output and prepare carousel payload
const aiOutput = $input.first().json.output;
const inputData = $('Parse News Input').first().json;

let carouselData;
try {
  // Clean JSON from markdown if present
  let cleanOutput = aiOutput.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
  const jsonMatch = cleanOutput.match(/\{[\s\S]*\}/);
  if (jsonMatch) cleanOutput = jsonMatch[0];
  carouselData = JSON.parse(cleanOutput);
} catch (e) {
  // Fallback structure if parsing fails
  carouselData = {
    topic: 'Technology Update',
    headline_main: 'Latest Tech Insights',
    context: 'Key technology updates for enterprise leaders.',
    slides: inputData.news_items.map((n, i) => ({
      headline: n.title.substring(0, 50),
      subtext: n.source || ''
    })),
    key_insights: 'Cloud | Security | Innovation',
    cta_text: 'Partner with MSI Technologies'
  };
}

// Remove any emojis that might have slipped through
const removeEmojis = (str) => {
  if (!str) return '';
  return str.replace(/[\u{1F600}-\u{1F64F}\u{1F300}-\u{1F5FF}\u{1F680}-\u{1F6FF}\u{1F1E0}-\u{1F1FF}\u{2600}-\u{26FF}\u{2700}-\u{27BF}\u{1F900}-\u{1F9FF}\u{1FA00}-\u{1FA6F}\u{1FA70}-\u{1FAFF}]/gu, '').trim();
};

// Clean all text fields
carouselData.topic = removeEmojis(carouselData.topic);
carouselData.headline_main = removeEmojis(carouselData.headline_main);
carouselData.context = removeEmojis(carouselData.context);
carouselData.key_insights = removeEmojis(carouselData.key_insights);
carouselData.cta_text = removeEmojis(carouselData.cta_text);
carouselData.slides = carouselData.slides.map(s => ({
  headline: removeEmojis(s.headline),
  subtext: removeEmojis(s.subtext)
}));

// Build carousel webhook payload with MSI brand colors
const carouselPayload = {
  topic: carouselData.topic,
  visual_style: inputData.visual_style,
  context: carouselData.context,
  slides: carouselData.slides,
  // MSI Brand Colors - MANDATORY for consistency
  color_primary: inputData.brand_colors.primary,
  color_secondary: inputData.brand_colors.secondary,
  color_accent: inputData.brand_colors.accent,
  color_dark: inputData.brand_colors.dark,
  // Visual consistency parameters
  font_headline: 'ITC Avant Garde Bold',
  font_subtext: 'Poppins SemiBold',
  font_body: 'Quicksand Medium',
  font_size_headline: '48-56pt',
  font_size_subtext: '24-32pt',
  logo_position: 'bottom-right',
  background_style: 'solid_primary',
  // Source data for tracking
  source_news_ids: inputData.news_items.map(n => n.id).filter(Boolean),
  generated_from: 'input-generator-flow'
};

return [{
  json: {
    carousel_payload: carouselPayload,
    generated_inputs: carouselData,
    request_id: inputData.request_id
  }
}];

// Process carousel response and prepare final output
const carouselResponse = $input.first().json;
const preparedData = $('Prepare Carousel Payload').first().json;

// Extract slides from carousel response
let slides = [];
if (carouselResponse.slides && Array.isArray(carouselResponse.slides)) {
  slides = carouselResponse.slides;
} else if (carouselResponse.results && Array.isArray(carouselResponse.results)) {
  slides = carouselResponse.results;
}

return [{
  json: {
    success: true,
    message: 'Carousel generated successfully from selected news',
    carousel_id: carouselResponse.carousel_id || preparedData.request_id,
    topic: preparedData.generated_inputs.topic,
    slides: slides,
    slide_count: slides.length,
    generated_inputs: preparedData.generated_inputs,
    visual_style: preparedData.carousel_payload.visual_style,
    brand_colors: {
      primary: preparedData.carousel_payload.color_primary,
      secondary: preparedData.carousel_payload.color_secondary,
      accent: preparedData.carousel_payload.color_accent
    }
  }
}];
