// ========================================
// CONFIGURATION - MSI Profile Interactions Scraper
// ========================================
// INSTRUCCIONES:
// 1. Reemplaza YOUR_LINKEDIN_PROFILE_URL con tu URL de LinkedIn
// 2. Configura tu método de scraping preferido
// 3. Para RapidAPI: crea cuenta en rapidapi.com y suscríbete a 'Fresh LinkedIn Profile Data'
// 4. Para Phantombuster: configura tu API key y agent IDs

const runId = `scrape_${Date.now()}_${Math.random().toString(36).substring(7)}`;

return [{
  json: {
    // === Tu perfil de LinkedIn ===
    linkedinProfileUrl: 'https://www.linkedin.com/in/TU-PERFIL/',
    linkedinCompanyUrl: 'https://www.linkedin.com/company/msi-technologies/',

    // === Método de scraping ===
    // Opciones: 'rapidapi', 'phantombuster', 'apify'
    scrapeMethod: 'rapidapi',

    // === RapidAPI Config (Fresh LinkedIn Profile Data) ===
    rapidapi: {
      host: 'fresh-linkedin-profile-data.p.rapidapi.com',
      // La API key se configura en las credenciales de n8n (Header Auth)
      maxPostsToScrape: 20,
      includeReactions: true,
      includeComments: true
    },

    // === Phantombuster Config (alternativa) ===
    phantombuster: {
      apiKey: '',
      postLikersAgentId: '',
      postCommentersAgentId: '',
      profileVisitorsAgentId: ''
    },

    // === Scrape run metadata ===
    runId: runId,
    scrapeSource: 'rapidapi',
    timestamp: new Date().toISOString()
  }
}];

// Pasar datos de configuración junto con el resultado del log
const config = $('Workflow Configuration').first().json;
const logResult = $input.first().json;

return [{
  json: {
    ...config,
    scrapeDbId: logResult.id || null
  }
}];

// Extraer posts y preparar para scraping de reacciones
const input = $input.first().json;
const config = $('Merge Config + Log').first().json;
const maxPosts = config.rapidapi?.maxPostsToScrape || 20;

let posts = [];

// Manejar diferentes formatos de respuesta de la API
if (input.data && Array.isArray(input.data)) {
  posts = input.data;
} else if (input.posts && Array.isArray(input.posts)) {
  posts = input.posts;
} else if (Array.isArray(input)) {
  posts = input;
}

console.log(`Found ${posts.length} posts, processing up to ${maxPosts}`);

const processedPosts = posts.slice(0, maxPosts).map((post, index) => {
  // Extraer URN/ID del post para consultar reacciones
  const postUrn = post.urn || post.post_urn || post.activity_id || post.id || '';
  const postUrl = post.post_url || post.url || post.share_url || '';
  const postText = (post.text || post.commentary || post.content || '').substring(0, 200);

  // Extraer reacciones directas si la API las incluye
  const reactions = post.reactions || post.likes || [];
  const comments = post.comments || [];
  const totalLikes = post.num_likes || post.likes_count || post.total_reactions || reactions.length || 0;
  const totalComments = post.num_comments || post.comments_count || comments.length || 0;
  const totalShares = post.num_shares || post.shares_count || post.reposts || 0;

  return {
    index: index + 1,
    postUrn,
    postUrl,
    postText,
    reactions,
    comments,
    totalLikes,
    totalComments,
    totalShares,
    postedAt: post.posted_at || post.created_at || post.date || null
  };
});

// Si no hay posts, devolver un item vacío para que el flujo continúe
if (processedPosts.length === 0) {
  return [{
    json: {
      posts: [],
      totalPosts: 0,
      runId: config.runId,
      scrapeDbId: config.scrapeDbId,
      scrapeSource: config.scrapeSource,
      hasData: false
    }
  }];
}

// Devolver cada post como item separado para procesamiento paralelo
return processedPosts.map(post => ({
  json: {
    ...post,
    runId: config.runId,
    scrapeDbId: config.scrapeDbId,
    scrapeSource: config.scrapeSource,
    linkedinProfileUrl: config.linkedinProfileUrl,
    rapidapiHost: config.rapidapi?.host,
    hasData: true
  }
}));

// Procesar reacciones de un post
const input = $input.first().json;
const postData = $('Extract Posts').first().json;

let reactors = [];

// Extraer reactores según formato de respuesta
const data = input.data || input.reactions || input.results || [];
const reactionList = Array.isArray(data) ? data : [];

for (const reactor of reactionList) {
  reactors.push({
    full_name: reactor.full_name || reactor.name || `${reactor.first_name || ''} ${reactor.last_name || ''}`.trim(),
    first_name: reactor.first_name || '',
    last_name: reactor.last_name || '',
    headline: reactor.headline || reactor.title || '',
    company: reactor.company || reactor.current_company || '',
    linkedin_url: reactor.linkedin_url || reactor.profile_url || reactor.url || '',
    linkedin_id: reactor.linkedin_id || reactor.member_id || '',
    avatar_url: reactor.profile_picture || reactor.avatar || reactor.image_url || '',
    location: reactor.location || '',
    connection_degree: reactor.degree || reactor.connection_degree || null,
    reaction_type: reactor.reaction_type || reactor.type || 'LIKE',
    interaction_type: 'reaction'
  });
}

console.log(`Post ${postData.index}: Found ${reactors.length} reactions`);

return [{
  json: {
    postIndex: postData.index,
    postUrl: postData.postUrl,
    postText: postData.postText,
    reactors: reactors,
    runId: postData.runId,
    scrapeDbId: postData.scrapeDbId,
    scrapeSource: postData.scrapeSource
  }
}];

// Procesar comentarios de un post
const input = $input.first().json;
const postData = $('Extract Posts').first().json;

let commenters = [];

// Extraer comentaristas según formato de respuesta
const data = input.data || input.comments || input.results || [];
const commentList = Array.isArray(data) ? data : [];

for (const comment of commentList) {
  const author = comment.author || comment.commenter || comment;
  commenters.push({
    full_name: author.full_name || author.name || `${author.first_name || ''} ${author.last_name || ''}`.trim(),
    first_name: author.first_name || '',
    last_name: author.last_name || '',
    headline: author.headline || author.title || '',
    company: author.company || author.current_company || '',
    linkedin_url: author.linkedin_url || author.profile_url || author.url || '',
    linkedin_id: author.linkedin_id || author.member_id || '',
    avatar_url: author.profile_picture || author.avatar || author.image_url || '',
    location: author.location || '',
    connection_degree: author.degree || author.connection_degree || null,
    comment_text: comment.text || comment.comment || comment.content || '',
    comment_url: comment.comment_url || comment.url || '',
    interaction_type: 'comment'
  });
}

console.log(`Post ${postData.index}: Found ${commenters.length} comments`);

return [{
  json: {
    postIndex: postData.index,
    postUrl: postData.postUrl,
    postText: postData.postText,
    commenters: commenters,
    runId: postData.runId,
    scrapeDbId: postData.scrapeDbId,
    scrapeSource: postData.scrapeSource
  }
}];

// Agregar todas las interacciones de todos los posts
const reactionItems = $('Process Reactions').all();
const commentItems = $('Process Comments').all();

const allInteractions = [];
const profileMap = new Map(); // Deduplicate by linkedin_url

let runId = '';
let scrapeDbId = '';
let scrapeSource = '';

// Procesar reacciones
for (const item of reactionItems) {
  const data = item.json;
  runId = data.runId || runId;
  scrapeDbId = data.scrapeDbId || scrapeDbId;
  scrapeSource = data.scrapeSource || scrapeSource;

  for (const reactor of (data.reactors || [])) {
    const key = reactor.linkedin_url || reactor.full_name;
    if (!key) continue;

    // Agregar/actualizar perfil
    if (!profileMap.has(key)) {
      profileMap.set(key, {
        full_name: reactor.full_name,
        first_name: reactor.first_name,
        last_name: reactor.last_name,
        headline: reactor.headline,
        company: reactor.company,
        linkedin_url: reactor.linkedin_url,
        linkedin_id: reactor.linkedin_id,
        avatar_url: reactor.avatar_url,
        location: reactor.location,
        connection_degree: reactor.connection_degree
      });
    }

    allInteractions.push({
      profile_key: key,
      interaction_type: 'reaction',
      reaction_type: reactor.reaction_type,
      post_url: data.postUrl,
      post_content_preview: data.postText,
      comment_text: null,
      comment_url: null
    });
  }
}

// Procesar comentarios
for (const item of commentItems) {
  const data = item.json;
  runId = data.runId || runId;

  for (const commenter of (data.commenters || [])) {
    const key = commenter.linkedin_url || commenter.full_name;
    if (!key) continue;

    if (!profileMap.has(key)) {
      profileMap.set(key, {
        full_name: commenter.full_name,
        first_name: commenter.first_name,
        last_name: commenter.last_name,
        headline: commenter.headline,
        company: commenter.company,
        linkedin_url: commenter.linkedin_url,
        linkedin_id: commenter.linkedin_id,
        avatar_url: commenter.avatar_url,
        location: commenter.location,
        connection_degree: commenter.connection_degree
      });
    }

    allInteractions.push({
      profile_key: key,
      interaction_type: 'comment',
      reaction_type: null,
      post_url: data.postUrl,
      post_content_preview: data.postText,
      comment_text: commenter.comment_text,
      comment_url: commenter.comment_url
    });
  }
}

console.log(`Total unique profiles: ${profileMap.size}`);
console.log(`Total interactions: ${allInteractions.length}`);

return [{
  json: {
    profiles: Array.from(profileMap.entries()).map(([key, profile]) => ({ key, ...profile })),
    interactions: allInteractions,
    totalProfiles: profileMap.size,
    totalInteractions: allInteractions.length,
    runId,
    scrapeDbId,
    scrapeSource
  }
}];

// UPSERT perfiles: insertar nuevos o actualizar existentes
const data = $input.first().json;
const profiles = data.profiles || [];

if (profiles.length === 0) {
  return [{ json: { profileIdMap: {}, newProfiles: 0, updatedProfiles: 0 } }];
}

// Construir query UPSERT masivo
const values = profiles.map((p, i) => {
  const esc = (s) => (s || '').replace(/'/g, "''").substring(0, 500);
  const deg = p.connection_degree ? parseInt(p.connection_degree) : 'NULL';
  return `(
    '${esc(p.linkedin_url)}',
    '${esc(p.linkedin_id)}',
    '${esc(p.full_name)}',
    '${esc(p.first_name)}',
    '${esc(p.last_name)}',
    '${esc(p.headline)}',
    '${esc(p.company)}',
    '${esc(p.location)}',
    '${esc(p.avatar_url)}',
    ${deg}
  )`;
}).join(',\n');

const query = `
INSERT INTO scraped_profiles (
  linkedin_url, linkedin_id, full_name, first_name, last_name,
  headline, company, location, avatar_url, connection_degree
)
VALUES ${values}
ON CONFLICT (linkedin_url) DO UPDATE SET
  full_name = COALESCE(NULLIF(EXCLUDED.full_name, ''), scraped_profiles.full_name),
  headline = COALESCE(NULLIF(EXCLUDED.headline, ''), scraped_profiles.headline),
  company = COALESCE(NULLIF(EXCLUDED.company, ''), scraped_profiles.company),
  avatar_url = COALESCE(NULLIF(EXCLUDED.avatar_url, ''), scraped_profiles.avatar_url),
  location = COALESCE(NULLIF(EXCLUDED.location, ''), scraped_profiles.location),
  connection_degree = COALESCE(EXCLUDED.connection_degree, scraped_profiles.connection_degree),
  last_interaction_at = NOW(),
  updated_at = NOW()
RETURNING id, linkedin_url, full_name,
  (xmax = 0) AS is_new
`;

return [{
  json: {
    query,
    interactions: data.interactions,
    totalProfiles: data.totalProfiles,
    totalInteractions: data.totalInteractions,
    runId: data.runId,
    scrapeDbId: data.scrapeDbId,
    scrapeSource: data.scrapeSource
  }
}];

// Crear mapa de linkedin_url -> profile_id desde el resultado del upsert
const upsertResults = $input.all();
const prevData = $('Prepare Profile Upsert').first().json;

const profileIdMap = {};
let newProfiles = 0;
let updatedProfiles = 0;

for (const item of upsertResults) {
  const row = item.json;
  if (row.linkedin_url) {
    profileIdMap[row.linkedin_url] = row.id;
    if (row.is_new) newProfiles++;
    else updatedProfiles++;
  }
}

// También mapear por full_name como fallback
for (const item of upsertResults) {
  const row = item.json;
  if (row.full_name && !profileIdMap[row.full_name]) {
    profileIdMap[row.full_name] = row.id;
  }
}

console.log(`Profile upsert complete: ${newProfiles} new, ${updatedProfiles} updated`);
console.log(`Profile ID map has ${Object.keys(profileIdMap).length} entries`);

return [{
  json: {
    profileIdMap,
    newProfiles,
    updatedProfiles,
    interactions: prevData.interactions,
    totalInteractions: prevData.totalInteractions,
    runId: prevData.runId,
    scrapeDbId: prevData.scrapeDbId,
    scrapeSource: prevData.scrapeSource
  }
}];

// Preparar INSERT masivo de interacciones
const data = $input.first().json;
const profileIdMap = data.profileIdMap || {};
const interactions = data.interactions || [];

if (interactions.length === 0) {
  return [{
    json: {
      query: null,
      interactionsToSave: 0,
      newProfiles: data.newProfiles,
      updatedProfiles: data.updatedProfiles,
      runId: data.runId,
      scrapeDbId: data.scrapeDbId
    }
  }];
}

const esc = (s) => (s || '').replace(/'/g, "''").substring(0, 1000);

const validInteractions = interactions.filter(i => {
  const profileId = profileIdMap[i.profile_key];
  return !!profileId;
});

if (validInteractions.length === 0) {
  return [{
    json: {
      query: null,
      interactionsToSave: 0,
      newProfiles: data.newProfiles,
      updatedProfiles: data.updatedProfiles,
      runId: data.runId,
      scrapeDbId: data.scrapeDbId
    }
  }];
}

const values = validInteractions.map(i => {
  const profileId = profileIdMap[i.profile_key];
  const reactionType = i.reaction_type ? `'${esc(i.reaction_type)}'` : 'NULL';
  const commentText = i.comment_text ? `'${esc(i.comment_text)}'` : 'NULL';
  const commentUrl = i.comment_url ? `'${esc(i.comment_url)}'` : 'NULL';
  const postUrl = i.post_url ? `'${esc(i.post_url)}'` : 'NULL';
  const postPreview = i.post_content_preview ? `'${esc(i.post_content_preview)}'` : 'NULL';
  const interactionType = i.interaction_type === 'reaction' ? 'reaction' : i.interaction_type;

  return `(
    '${profileId}',
    '${interactionType}',
    ${reactionType},
    ${postUrl},
    ${postPreview},
    ${commentText},
    ${commentUrl},
    '${esc(data.runId)}',
    '${data.scrapeSource || 'rapidapi'}',
    NOW()
  )`;
}).join(',\n');

const query = `
INSERT INTO profile_interactions (
  profile_id, interaction_type, reaction_type,
  post_url, post_content_preview,
  comment_text, comment_url,
  scrape_run_id, scraped_from, interaction_date
)
VALUES ${values}
ON CONFLICT DO NOTHING
RETURNING id
`;

console.log(`Preparing to insert ${validInteractions.length} interactions`);

return [{
  json: {
    query,
    interactionsToSave: validInteractions.length,
    newProfiles: data.newProfiles,
    updatedProfiles: data.updatedProfiles,
    runId: data.runId,
    scrapeDbId: data.scrapeDbId
  }
}];

// Contar interacciones guardadas
const insertResults = $input.all();
const prevData = $('Prepare Interactions Insert').first().json;

const savedCount = insertResults.length;

console.log(`Saved ${savedCount} interactions to DB`);

return [{
  json: {
    interactionsSaved: savedCount,
    interactionsAttempted: prevData.interactionsToSave,
    newProfiles: prevData.newProfiles,
    updatedProfiles: prevData.updatedProfiles,
    runId: prevData.runId,
    scrapeDbId: prevData.scrapeDbId
  }
}];

// No interactions to save path
const prevData = $('Prepare Interactions Insert').first().json;

return [{
  json: {
    interactionsSaved: 0,
    interactionsAttempted: 0,
    newProfiles: prevData.newProfiles,
    updatedProfiles: prevData.updatedProfiles,
    runId: prevData.runId,
    scrapeDbId: prevData.scrapeDbId
  }
}];

// No data path - update log as completed with 0 results
const config = $('Merge Config + Log').first().json;

return [{
  json: {
    interactionsSaved: 0,
    interactionsAttempted: 0,
    newProfiles: 0,
    updatedProfiles: 0,
    runId: config.runId,
    scrapeDbId: config.scrapeDbId,
    message: 'No posts found to scrape'
  }
}];

// Final summary
const logResult = $input.first().json;
const prevItems = $('Count Saved').all();
const noInteractionItems = $('No Interactions').all();

// Get data from whichever path was taken
let stats;
if (prevItems.length > 0) {
  stats = prevItems[0].json;
} else if (noInteractionItems.length > 0) {
  stats = noInteractionItems[0].json;
} else {
  stats = { newProfiles: 0, updatedProfiles: 0, interactionsSaved: 0 };
}

return [{
  json: {
    success: true,
    message: 'Profile interactions scrape completed',
    stats: {
      profiles_found: (stats.newProfiles || 0) + (stats.updatedProfiles || 0),
      new_profiles: stats.newProfiles || 0,
      updated_profiles: stats.updatedProfiles || 0,
      interactions_saved: stats.interactionsSaved || 0,
      duration_seconds: logResult.duration_seconds || 0
    },
    run_id: stats.runId || logResult.run_id,
    timestamp: new Date().toISOString()
  }
}];
