// Prepare Gemini Input - Build complete slide image prompt
const inputData = $('Webhook - Slide Gen').item.json;
const input = inputData.body || inputData;
const logoItem = $('Download MSI Logo').item;

// Get logo base64
let logoBase64 = '';
if (logoItem.binary && Object.keys(logoItem.binary).length > 0) {
  const binaryKey = Object.keys(logoItem.binary)[0];
  const binaryData = logoItem.binary[binaryKey];

  try {
    const buffer = await this.helpers.getBinaryDataBuffer(0, binaryKey);
    logoBase64 = buffer.toString('base64');
  } catch (e) {
     if (binaryData.data && binaryData.data !== 'filesystem-v2') {
         logoBase64 = binaryData.data;
     }
  }
}

// Extract all slide content fields
const slideNumber = parseInt(input.slide_number) || 1;
const headline = input.headline || '';
const subtext = input.subtext || '';
const bodyText = input.body_text || '';
const topic = input.topic || 'Technology';

// CRITICAL: Get the specific image_prompt from Agent 3 (Visual Director)
// This contains a detailed, news-specific scene description
const agentImagePrompt = input.image_prompt || '';

// Extract source from visual_style JSON
let source = '';
try {
  if (input.visual_style) {
    const vs = typeof input.visual_style === 'string' ? JSON.parse(input.visual_style) : input.visual_style;
    source = vs.source || '';
  }
} catch (e) {
  source = '';
}
if (!source || source.trim() === '') {
  source = 'Industry Report';
}

// Generate dynamic tagline for Slide 1 based on topic
const topicLower = topic.toLowerCase();
let topicTagline = 'Our Latest Tech News';
if (topicLower.includes('ai') || topicLower.includes('artificial intelligence') || topicLower.includes('machine learning')) {
  topicTagline = 'Our Latest AI News';
} else if (topicLower.includes('security') || topicLower.includes('cyber')) {
  topicTagline = 'Our Latest Security News';
} else if (topicLower.includes('cloud')) {
  topicTagline = 'Our Latest Cloud News';
} else if (topicLower.includes('data')) {
  topicTagline = 'Our Latest Data News';
} else if (topicLower.includes('digital') || topicLower.includes('transformation')) {
  topicTagline = 'Our Latest Digital News';
} else if (topicLower.includes('bpo') || topicLower.includes('outsourcing')) {
  topicTagline = 'Our Latest Business News';
} else if (topicLower.includes('blockchain') || topicLower.includes('crypto')) {
  topicTagline = 'Our Latest Blockchain News';
} else if (topicLower.includes('automation') || topicLower.includes('rpa')) {
  topicTagline = 'Our Latest Automation News';
}

const isBlueSlide = (slideNumber === 1 || slideNumber === 3 || slideNumber === 5);
const paginationNumber = slideNumber;

// Build a specific photo description from Agent 3's prompt + headline context
// If Agent 3 provided a specific scene, use it. Otherwise build from headline.
const photoScene = agentImagePrompt 
  ? agentImagePrompt 
  : `A realistic editorial photograph that visually represents this specific news: "${headline}". ${bodyText ? 'Context: ' + bodyText : ''} The photo must be specific to this story, not generic.`;

let fullPrompt = '';

if (slideNumber === 1) {
  fullPrompt = `Create a 4:5 Instagram carousel SLIDE 1 for MSI Technologies.

LOGO (CRITICAL - DO NOT MODIFY):
- Place the attached MSI logo EXACTLY as provided in TOP-LEFT corner
- Logo size: approximately 60-80 pixels height, proportional width
- DO NOT resize, redraw, recreate, or modify the logo in ANY way
- Use the EXACT logo file provided - no alterations

LAYOUT:
- ENTIRE SLIDE: Gradient background from MSI Blue #207CE5 to Dark Blue #004AAD
- MAIN VISUAL: A striking 3D ANIMATED-STYLE element that represents the SPECIFIC topic of this carousel: "${headline}"
- The 3D object must be UNIQUE, CREATIVE, and DIRECTLY connected to what the headline is about. Think: what physical object or concept best symbolizes "${headline}"? Create a 3D version of THAT.
- NOT centered - position creatively (top-right, bottom-left, or asymmetric)
- VARY THE OBJECT: must be completely different from any standard tech icon. Be creative and unexpected.
- 3D ELEMENT COLORS: WHITE, CREAM #FFFDF1, or SILVER tones ONLY. Must CONTRAST against blue.
- Glossy/metallic finish, subtle glow, floating with depth.

FORBIDDEN 3D ELEMENTS (these are overused clichÃ©s):
- NO servers, NO cloud shapes, NO network lines, NO circuit boards, NO binary code
- NO globe with connections, NO gears, NO floating screens, NO lock/shield icons
- NO generic tech symbols. The 3D element must be SPECIFIC to this headline.

TYPOGRAPHY (MANDATORY):
- HEADINGS: ITC Avant Garde font (bold, geometric sans-serif)
- BODY/CONTENT: Quicksand font (rounded, friendly sans-serif)
- ALL text NORMAL proportions - NOT stretched, NOT condensed, NOT elongated

TEXT CONTENT (white color):
- VERY LARGE bold title in ITC Avant Garde: "${headline}" - Position to COMPLEMENT the 3D element
- Smaller tagline below in Quicksand: "${topicTagline}"
- Bottom-RIGHT corner: "Swipe >>>" in Quicksand

FORBIDDEN: Stretched text, condensed fonts, blue 3D elements, gold elements, people, photographs.`;

} else if (slideNumber === 5) {
  fullPrompt = `Create a 4:5 Instagram carousel SLIDE 5 - CTA for MSI Technologies.

LOGO (CRITICAL - DO NOT MODIFY):
- Place the attached MSI logo EXACTLY as provided in TOP-LEFT corner
- Logo size: approximately 60-80 pixels height, proportional width
- DO NOT resize, redraw, recreate, or modify the logo in ANY way

LAYOUT:
- ENTIRE SLIDE: Gradient background from MSI Blue #207CE5 to Dark Blue #004AAD
- NO photo - solid gradient only
- Content vertically centered

TYPOGRAPHY (MANDATORY):
- HEADINGS: ITC Avant Garde font (bold, geometric sans-serif)
- BODY/CONTENT: Quicksand font (rounded, friendly sans-serif)
- ALL text NORMAL proportions

TEXT CONTENT (white, centered):
- HEADING 1 (LARGE, bold, ITC Avant Garde): "${headline}"
- HEADING 2 (Medium, below heading 1, Quicksand, REGULAR weight - NOT BOLD): "${subtext || "Let's Work Together"}"

CONTACT INFO (bottom center, medium-small white Quicksand - elegant, not oversized):
- Line 1: envelope icon then contact@msitechnologiesinc.com
- Line 2: globe icon then msitechnologiesinc.com

FORBIDDEN: Stretched text, condensed fonts, @ symbols, pagination, modifying logo, Follow Us text.`;

} else if (slideNumber === 2 || slideNumber === 4) {
  fullPrompt = `Create a 4:5 Instagram carousel SLIDE ${slideNumber} for MSI Technologies.

LOGO (CRITICAL - DO NOT MODIFY):
- Place the attached MSI logo (blue version) EXACTLY as provided in TOP-LEFT corner, overlaying the photo
- Logo size: approximately 60-80 pixels height, proportional width
- DO NOT resize, redraw, recreate, or modify the logo in ANY way

CRITICAL LAYOUT:
- The photo MUST start at the VERY TOP (y=0). NO white bar above photo.
- Top 55%: SPECIFIC photo described below. Photo touches TOP and SIDE edges. AVOID PEOPLE.
- Apply subtle BLUE GRADIENT at bottom edge of photo blending into white.
- Bottom 45%: Clean WHITE background for text.

PHOTO DESCRIPTION (THIS IS THE SPECIFIC SCENE TO SHOW):
${photoScene}

PHOTO STYLE: High-end editorial photography (think Bloomberg, Harvard Business Review, Wired). Strategic, evocative, cinematic. The photo should visually represent the BUSINESS INSIGHT, not just the news topic.
FORBIDDEN PHOTOS: NO generic office spaces, NO generic tech imagery, NO servers, NO cloud shapes, NO circuit boards, NO binary code, NO handshakes, NO people pointing at screens, NO stock photo clichÃ©s. The photo MUST visually represent the STRATEGIC ANGLE of the headline: "${headline}"

TYPOGRAPHY (MANDATORY):
- HEADINGS: ITC Avant Garde font (bold, geometric sans-serif)
- BODY/CONTENT: Quicksand font (rounded, friendly sans-serif)
- ALL text NORMAL proportions

TEXT (on white section, left-aligned with padding):
- Headline in ITC Avant Garde BOLD (blue #207CE5, 18-20pt equivalent): "${headline}"
- Body text in Quicksand REGULAR (dark gray #333333, 12-13pt equivalent - SMALL ENOUGH to fit 3-4 lines comfortably): "${bodyText}"
- Source in Quicksand (very small 9pt, light gray #999): "Source: ${source}"
- Bottom-right in Quicksand REGULAR weight (NOT BOLD, small): "${paginationNumber}/5"

BODY TEXT SIZING (CRITICAL):
- The body text is 3-4 sentences. Use a SMALL font size (12-13pt) so ALL lines fit in the white area.
- DO NOT truncate or cut body text. ALL text must be visible.
- Leave enough vertical space between headline and body, and between body and source.

FORBIDDEN: White bar above photo. Generic photos. Modifying logo. Bold pagination. Truncated body text.`;

} else {
  fullPrompt = `Create a 4:5 Instagram carousel SLIDE ${slideNumber} for MSI Technologies.

LOGO (CRITICAL - DO NOT MODIFY):
- Place the attached MSI logo EXACTLY as provided in TOP-LEFT corner
- Logo size: approximately 60-80 pixels height, proportional width
- DO NOT resize, redraw, recreate, or modify the logo in ANY way

LAYOUT:
- Top 55%: SPECIFIC photo described below. AVOID PEOPLE. Apply BLUE GRADIENT overlay at bottom blending into blue section.
- Bottom 45%: Solid MSI Blue #207CE5

PHOTO DESCRIPTION (THIS IS THE SPECIFIC SCENE TO SHOW):
${photoScene}

PHOTO STYLE: High-end editorial photography (think Bloomberg, Harvard Business Review, Wired). Strategic, evocative, cinematic. The photo should visually represent the BUSINESS INSIGHT, not just the news topic.
FORBIDDEN PHOTOS: NO generic office spaces, NO generic tech imagery, NO servers, NO cloud shapes, NO circuit boards, NO binary code, NO handshakes, NO stock photo clichÃ©s. The photo MUST visually represent the STRATEGIC ANGLE of the headline: "${headline}"

TYPOGRAPHY (MANDATORY):
- HEADINGS: ITC Avant Garde font (bold, geometric sans-serif)
- BODY/CONTENT: Quicksand font (rounded, friendly sans-serif)
- ALL text NORMAL proportions

TEXT (on blue section, left-aligned with padding):
- Headline in ITC Avant Garde BOLD (white, 18-20pt equivalent): "${headline}"
- Body text in Quicksand REGULAR (cream #FFFDF1, 12-13pt equivalent - SMALL ENOUGH to fit 3-4 lines comfortably): "${bodyText}"
- Source in Quicksand (very small 9pt, light cream): "Source: ${source}"
- Bottom-right in Quicksand REGULAR weight (NOT BOLD, small): "${paginationNumber}/5"

BODY TEXT SIZING (CRITICAL):
- The body text is 3-4 sentences. Use a SMALL font size (12-13pt) so ALL lines fit in the blue area.
- DO NOT truncate or cut body text. ALL text must be visible.
- Leave enough vertical space between headline and body, and between body and source.

STYLE: Clean, minimal, professional. AVOID people.`;
}

return [{
  json: {
    image_prompt: fullPrompt,
    slide_id: input.slide_id,
    carousel_id: input.carousel_id,
    slide_number: slideNumber,
    headline: headline,
    subtext: subtext,
    body_text: bodyText,
    source: source,
    topic: topic,
    topic_tagline: topicTagline,
    is_blue_slide: isBlueSlide,
    logo_base64: logoBase64
  }
}];

