// Prepare Gemini Input - Build complete slide image prompt
const inputData = $('Webhook - Slide Gen').item.json;
const input = inputData.body || inputData;
const logoItem = $('Download MSI Logo').item;

// Get logo base64
let logoBase64 = '';
if (logoItem.binary && Object.keys(logoItem.binary).length > 0) {
  const binaryKey = Object.keys(logoItem.binary)[0];
  const binaryData = logoItem.binary[binaryKey];

  try {
    const buffer = await this.helpers.getBinaryDataBuffer(0, binaryKey);
    logoBase64 = buffer.toString('base64');
  } catch (e) {
     if (binaryData.data && binaryData.data !== 'filesystem-v2') {
         logoBase64 = binaryData.data;
     }
  }
}

// Extract all slide content fields
const slideNumber = parseInt(input.slide_number) || 1;
const headline = input.headline || '';
const subtext = input.subtext || '';
const bodyText = input.body_text || '';
const source = input.source || '';
const topic = input.topic || 'Technology';

// Generate dynamic header based on topic
const topicLower = topic.toLowerCase();
let dynamicHeader = 'Latest in Tech';
if (topicLower.includes('ai') || topicLower.includes('artificial intelligence') || topicLower.includes('machine learning')) {
  dynamicHeader = 'AI & Machine Learning';
} else if (topicLower.includes('security') || topicLower.includes('cyber')) {
  dynamicHeader = 'Security Updates';
} else if (topicLower.includes('cloud')) {
  dynamicHeader = 'Cloud Computing';
} else if (topicLower.includes('data')) {
  dynamicHeader = 'Data & Analytics';
} else if (topicLower.includes('digital') || topicLower.includes('transformation')) {
  dynamicHeader = 'Digital Innovation';
} else if (topicLower.includes('bpo') || topicLower.includes('outsourcing')) {
  dynamicHeader = 'Business Solutions';
} else if (topicLower.includes('blockchain') || topicLower.includes('crypto')) {
  dynamicHeader = 'Blockchain & Web3';
} else if (topicLower.includes('automation') || topicLower.includes('rpa')) {
  dynamicHeader = 'Automation & RPA';
}

// Build the COMPLETE image generation prompt based on slide type
let fullPrompt = '';

if (slideNumber === 1) {
  // COVER SLIDE - No pagination, swipe indicator on RIGHT, header only (no text in header)
  fullPrompt = `Create a professional Instagram carousel COVER slide (4:5 aspect ratio, 1080x1350 pixels) for MSI Technologies.

DESIGN SPECIFICATIONS:
- Background: Clean WHITE (#FFFFFF) - absolutely NO cream or off-white colors
- Top section: MSI Technologies header bar in blue (#207CE5) - EMPTY header, just the blue bar with MSI logo, NO additional text
- Main visual area: Abstract tech-related imagery (circuits, data visualization, modern architecture, or technology patterns) - subtle, professional, not overwhelming
- NO PAGINATION INDICATOR on this slide (no 1/5)

TEXT CONTENT TO DISPLAY:
- Main Headline (large, bold, centered in middle of slide): "${headline}"
- Typography: Bold sans-serif font for headline
- Headline Color: Dark text (#2B2B2B)

BRANDING ELEMENTS:
- MSI Logo: Visible in the blue header bar at top
- SWIPE INDICATOR: Arrow icon pointing RIGHT with "Swipe" text, positioned on the RIGHT SIDE of the slide (not left), clearly visible, in gray or blue color

STYLE: Clean, minimalist, professional corporate design. NO text shadows. Flat design. Modern tech aesthetic.`;

} else if (slideNumber === 5) {
  // CTA SLIDE - No pagination, contact icons, CTA in headline only (no body below)
  fullPrompt = `Create a professional Instagram carousel CTA/closing slide (4:5 aspect ratio, 1080x1350 pixels) for MSI Technologies.

DESIGN SPECIFICATIONS:
- Background: Clean WHITE (#FFFFFF) - absolutely NO cream color
- Top section: MSI Technologies header bar in blue (#207CE5)
- NO PAGINATION INDICATOR on this slide (no 5/5)
- Clean, action-focused layout

TEXT CONTENT TO DISPLAY:
- CTA Headline (large, bold, action-oriented, centered): "${headline}"
- NO body text below the headline - the CTA headline is the ONLY main text element
- Typography: Bold sans-serif for CTA headline
- CTA Color: MSI Blue (#207CE5) or Dark (#2B2B2B)

CONTACT SECTION (at bottom with icons):
- Email row: Envelope icon (‚úâ) + "contact@msitechnologiesinc.com"
- Website row: Globe icon (üåê) + "msitechnologiesinc.com"
- Icons should be in MSI Blue (#207CE5)
- Contact text in dark gray (#2B2B2B)

BRANDING:
- MSI Logo: Prominently displayed, larger than other slides, centered or in header

STYLE: Clean, minimalist, professional call-to-action design. NO shadows. Flat design. Inviting and professional.`;

} else {
  // CONTENT SLIDES 2-4 - With pagination, dynamic header, source citation
  fullPrompt = `Create a professional Instagram carousel CONTENT slide (4:5 aspect ratio, 1080x1350 pixels) for MSI Technologies.

DESIGN SPECIFICATIONS:
- Background: Clean WHITE (#FFFFFF) - absolutely NO cream or off-white colors
- Top section: MSI Technologies header bar in blue (#207CE5) with text "${dynamicHeader}" in white
- Layout: Split design - approximately 55% top for visual imagery, 45% bottom for text content
- PAGINATION: "${slideNumber}/5" in top right corner (regular weight font, not bold, small size)

VISUAL AREA (top 55%):
- Professional imagery related to: ${headline}
- Style: Realistic photography, clean tech illustrations, or relevant business imagery
- Should be subtle and support the text, not overwhelming
- Related to the news topic being discussed

TEXT CONTENT AREA (bottom 45%):
- Headline: "${headline}" (bold, MSI Blue #207CE5 color)
- Body Text: "${bodyText}" (regular weight, dark gray #2B2B2B, max 3 lines, readable font size)
- Source citation: "Source: ${source}" (smaller text, light gray #888888, at bottom of text area)

BRANDING:
- MSI Logo: Bottom right corner, small but visible
- Typography: Sans-serif fonts throughout

STYLE: Clean, minimalist, professional news/information design. NO text shadows. Flat design. Easy to read.`;
}

return [{
  json: {
    image_prompt: fullPrompt,
    slide_id: input.slide_id,
    carousel_id: input.carousel_id,
    slide_number: slideNumber,
    headline: headline,
    subtext: subtext,
    body_text: bodyText,
    source: source,
    topic: topic,
    dynamic_header: dynamicHeader,
    logo_base64: logoBase64
  }
}];

