// Prepare Gemini Input - Build complete slide image prompt
const inputData = $('Webhook - Slide Gen').item.json;
const input = inputData.body || inputData;
const logoItem = $('Download MSI Logo').item;

// Get logo base64
let logoBase64 = '';
if (logoItem.binary && Object.keys(logoItem.binary).length > 0) {
  const binaryKey = Object.keys(logoItem.binary)[0];
  const binaryData = logoItem.binary[binaryKey];

  try {
    const buffer = await this.helpers.getBinaryDataBuffer(0, binaryKey);
    logoBase64 = buffer.toString('base64');
  } catch (e) {
     if (binaryData.data && binaryData.data !== 'filesystem-v2') {
         logoBase64 = binaryData.data;
     }
  }
}

// Extract all slide content fields
const slideNumber = parseInt(input.slide_number) || 1;
const headline = input.headline || '';
const subtext = input.subtext || '';
const bodyText = input.body_text || '';
const topic = input.topic || 'Technology';

// Extract source from visual_style JSON - CRITICAL: source is stored here
let source = '';
try {
  if (input.visual_style) {
    const vs = typeof input.visual_style === 'string' ? JSON.parse(input.visual_style) : input.visual_style;
    source = vs.source || '';
  }
} catch (e) {
  source = '';
}
// Fallback - if source is empty or just whitespace, use default
if (!source || source.trim() === '') {
  source = 'Industry Report';
}

// Generate dynamic tagline for Slide 1 based on topic
const topicLower = topic.toLowerCase();
let topicTagline = 'Our Latest Tech News';
if (topicLower.includes('ai') || topicLower.includes('artificial intelligence') || topicLower.includes('machine learning')) {
  topicTagline = 'Our Latest AI News';
} else if (topicLower.includes('security') || topicLower.includes('cyber')) {
  topicTagline = 'Our Latest Security News';
} else if (topicLower.includes('cloud')) {
  topicTagline = 'Our Latest Cloud News';
} else if (topicLower.includes('data')) {
  topicTagline = 'Our Latest Data News';
} else if (topicLower.includes('digital') || topicLower.includes('transformation')) {
  topicTagline = 'Our Latest Digital News';
} else if (topicLower.includes('bpo') || topicLower.includes('outsourcing')) {
  topicTagline = 'Our Latest Business News';
} else if (topicLower.includes('blockchain') || topicLower.includes('crypto')) {
  topicTagline = 'Our Latest Blockchain News';
} else if (topicLower.includes('automation') || topicLower.includes('rpa')) {
  topicTagline = 'Our Latest Automation News';
}

// Determine background color: Slides 1,3,5 = BLUE / Slides 2,4 = WHITE
const isBlueSlide = (slideNumber === 1 || slideNumber === 3 || slideNumber === 5);

// TYPOGRAPHY SPECS - MSI Brand Fonts (consistent across all slides)
// CRITICAL FOR IMAGE GENERATION: Use these EXACT font names
const typoSpecs = `
TYPOGRAPHY - USE THESE EXACT FONTS:
- ALL HEADINGS: Use "ITC Avant Garde Gothic" font (or Avant Garde, Century Gothic as fallback). Bold weight, 32-36pt size.
- ALL BODY/PARAGRAPHS: Use "Quicksand" font. Regular weight, 18-20pt size, max 3 lines.
- Source text: Quicksand font, 12-14pt, Regular weight.
- Tagline/Subtext: Quicksand font, 14-16pt, Regular weight.
- Pagination: Quicksand font, 14pt, Regular weight (NOT bold).
- IMPORTANT: Maintain consistent font sizes across ALL 5 slides for visual coherence.
- ALL TEXT must be LEFT-ALIGNED.
- NO text shadows, NO effects, NO outlines.
`;

// Pagination for content slides (2,3,4): shows slide number directly
const paginationNumber = slideNumber; // Slide 2 = "2", Slide 3 = "3", Slide 4 = "4"

// Build the COMPLETE image generation prompt based on slide type
let fullPrompt = '';

if (slideNumber === 1) {
  // COVER SLIDE - Blue background, NO photo
  fullPrompt = `Create a 4:5 Instagram carousel SLIDE 1 - HOOK SLIDE for MSI Technologies.

CRITICAL LOGO INSTRUCTION: I am providing the MSI Technologies logo as an attached image. Use ONLY this exact logo in TOP-LEFT corner at 8% width. DO NOT generate any other logo.

VISUAL STYLE: Photorealistic, professional.
PHOTO (top 60%): Business team in modern office looking at screens, natural light, 85mm lens. NO logos in photo area.

COLORS: MSI Blue #207CE5, White, Cream #F5F5DC.

LAYOUT:
- Top 60%: Photo (starts at pixel 0, NO header bar above it)
- Bottom 40%: Solid #207CE5 blue
${typoSpecs}
TEXT ON BLUE SECTION (LEFT-ALIGNED):
1) HEADLINE: "${headline}" - 48px, ITC Avant Garde Bold, WHITE
2) HOOK: "${topicTagline}" - 24px, Quicksand Regular, cream #F5F5DC
3) SWIPE: "Swipe >>>" - 14px, Quicksand, cream, bottom-left

Logo: TOP-LEFT 8% width, white version.
NO PAGINATION on cover slide.`;

} else if (slideNumber === 5) {
  // CTA SLIDE - Blue background with photo
  fullPrompt = `Create a 4:5 Instagram carousel SLIDE 5 - CTA SLIDE for MSI Technologies.

CRITICAL LOGO INSTRUCTION: Use ONLY the attached MSI logo in TOP-LEFT at 8% width. DO NOT generate any other logo.

PHOTO (top 55%): Professional handshake toward camera, warm smile, modern office, 85mm lens, soft light. NO logos in photo.

COLORS: MSI Blue #207CE5, White, Cream #F5F5DC.

LAYOUT:
- Top 55%: Photo (starts at pixel 0, NO header bar above it)
- Bottom 45%: Solid #207CE5 blue
${typoSpecs}
TEXT ON BLUE (LEFT-ALIGNED):
1) CTA: "${headline}" - 42px, ITC Avant Garde Bold, WHITE
2) EMAIL: "contact@msitechnologiesinc.com" - 20px, Quicksand Regular, cream
3) WEB: "msitechnologiesinc.com" - 20px, Quicksand Regular, cream

Logo: TOP-LEFT 8% width, white version.
Indicator: "5/5" 12px Quicksand bottom-right, "Follow Us" 14px cream bottom-left.`;

} else {
  // CONTENT SLIDES 2, 3, 4 - ALL have BLUE background for text area
  fullPrompt = `Create a 4:5 Instagram carousel SLIDE ${slideNumber} - NEWS SLIDE for MSI Technologies.

CRITICAL LOGO INSTRUCTION: Use ONLY the attached MSI logo in TOP-LEFT at 6% width. DO NOT generate any other logo.

VISUAL: Professional photography related to: ${headline}
PHOTO (top 55%): Realistic image matching context, 85mm lens. NO logos in photo area.

COLORS: MSI Blue #207CE5, White, Cream #F5F5DC.

LAYOUT:
- Top 55%: Photo (starts at pixel 0, NO header bar, NO strip, NO section above the photo)
- Bottom 45%: Solid #207CE5 blue
${typoSpecs}
TEXT ON BLUE (LEFT-ALIGNED):
1) HEADLINE: "${headline}" - 36px, ITC Avant Garde Bold, WHITE
2) INSIGHT: "${bodyText}" - 18px, Quicksand Regular, cream #F5F5DC, max 3 lines
3) SOURCE: "Source: ${source}" - 12px, Quicksand Regular, light cream

Logo: TOP-LEFT 6% width, white version.
Indicator: "${paginationNumber}/5" 12px Quicksand bottom-right, ">>>" 14px cream bottom-left.`;
}

return [{
  json: {
    image_prompt: fullPrompt,
    slide_id: input.slide_id,
    carousel_id: input.carousel_id,
    slide_number: slideNumber,
    headline: headline,
    subtext: subtext,
    body_text: bodyText,
    source: source,
    topic: topic,
    topic_tagline: topicTagline,
    is_blue_slide: isBlueSlide,
    logo_base64: logoBase64
  }
}];

