// Prepare Gemini Input - Build complete slide image prompt
const inputData = $('Webhook - Slide Gen').item.json;
const input = inputData.body || inputData;
const logoItem = $('Download MSI Logo').item;

// Get logo base64
let logoBase64 = '';
if (logoItem.binary && Object.keys(logoItem.binary).length > 0) {
  const binaryKey = Object.keys(logoItem.binary)[0];
  const binaryData = logoItem.binary[binaryKey];

  try {
    const buffer = await this.helpers.getBinaryDataBuffer(0, binaryKey);
    logoBase64 = buffer.toString('base64');
  } catch (e) {
     if (binaryData.data && binaryData.data !== 'filesystem-v2') {
         logoBase64 = binaryData.data;
     }
  }
}

// Extract all slide content fields
const slideNumber = parseInt(input.slide_number) || 1;
const headline = input.headline || '';
const subtext = input.subtext || '';
const bodyText = input.body_text || '';
const topic = input.topic || 'Technology';

// Extract source from visual_style JSON - CRITICAL: source is stored here
let source = '';
try {
  if (input.visual_style) {
    const vs = typeof input.visual_style === 'string' ? JSON.parse(input.visual_style) : input.visual_style;
    source = vs.source || '';
  }
} catch (e) {
  source = '';
}
// Fallback - if source is empty or just whitespace, use default
if (!source || source.trim() === '') {
  source = 'Industry Report';
}

// Generate dynamic tagline for Slide 1 based on topic
const topicLower = topic.toLowerCase();
let topicTagline = 'Our Latest Tech News';
if (topicLower.includes('ai') || topicLower.includes('artificial intelligence') || topicLower.includes('machine learning')) {
  topicTagline = 'Our Latest AI News';
} else if (topicLower.includes('security') || topicLower.includes('cyber')) {
  topicTagline = 'Our Latest Security News';
} else if (topicLower.includes('cloud')) {
  topicTagline = 'Our Latest Cloud News';
} else if (topicLower.includes('data')) {
  topicTagline = 'Our Latest Data News';
} else if (topicLower.includes('digital') || topicLower.includes('transformation')) {
  topicTagline = 'Our Latest Digital News';
} else if (topicLower.includes('bpo') || topicLower.includes('outsourcing')) {
  topicTagline = 'Our Latest Business News';
} else if (topicLower.includes('blockchain') || topicLower.includes('crypto')) {
  topicTagline = 'Our Latest Blockchain News';
} else if (topicLower.includes('automation') || topicLower.includes('rpa')) {
  topicTagline = 'Our Latest Automation News';
}

// Determine background color: Slides 1,3,5 = BLUE / Slides 2,4 = WHITE
const isBlueSlide = (slideNumber === 1 || slideNumber === 3 || slideNumber === 5);

// Pagination for content slides (2,3,4): shows slide number directly
const paginationNumber = slideNumber; // Slide 2 = "2", Slide 3 = "3", Slide 4 = "4"

// Build the COMPLETE image generation prompt based on slide type
let fullPrompt = '';

if (slideNumber === 1) {
  // COVER SLIDE - Photo with blue gradient + blue text area
  fullPrompt = `Create a 4:5 Instagram carousel SLIDE 1 for MSI Technologies.

LOGO: Use the attached MSI logo in TOP-LEFT corner, small size. Do not modify it.

LAYOUT:
- Top 60%: Professional photo of modern tech office with screens, apply BLUE GRADIENT overlay fading to bottom
- Bottom 40%: Solid MSI Blue #207CE5

TEXT (on blue section, left-aligned, white color):
- Large title: "${headline}"
- Smaller tagline below: "${topicTagline}"
- Bottom-left: "Swipe >>>"

STYLE: Clean, minimal, professional. No decorative elements.`;

} else if (slideNumber === 5) {
  // CTA SLIDE - NO photo, solid blue background only
  fullPrompt = `Create a 4:5 Instagram carousel SLIDE 5 - CTA for MSI Technologies.

LOGO: Use the attached MSI logo in TOP-LEFT corner, small size, white version. Do not modify it.

LAYOUT:
- ENTIRE SLIDE: Solid MSI Blue #207CE5 background
- NO photo, NO image - just solid blue color
- Centered content

TEXT (centered, white):
- Main CTA title: "${headline}"
- Below: "${bodyText}"

CONTACT INFO (bottom center, with icons):
- Envelope icon + "contact@msitechnologiesinc.com"
- Globe icon + "msitechnologiesinc.com"

- Bottom-right: "5/5"
- Bottom-left: "Follow Us"

STYLE: Clean, minimal, professional. Icons should be simple white line icons.`;

} else if (slideNumber === 2 || slideNumber === 4) {
  // CONTENT SLIDES 2, 4 - WHITE background for text area
  fullPrompt = `Create a 4:5 Instagram carousel SLIDE ${slideNumber} for MSI Technologies.

LOGO: Use the attached MSI logo in TOP-LEFT corner, small size, blue version. Do not modify it.

LAYOUT:
- Top 55%: Professional photo related to "${headline}", apply subtle BLUE GRADIENT overlay at bottom of photo
- Bottom 45%: Clean WHITE background

TEXT (on white section, left-aligned):
- Headline (blue #207CE5): "${headline}"
- Body text (dark gray): "${bodyText}"
- Source (small, gray): "Source: ${source}"

- Bottom-right: "${paginationNumber}/5"
- Bottom-left: ">>>"

STYLE: Clean, minimal, professional. No decorative elements.`;

} else {
  // CONTENT SLIDE 3 - BLUE background for text area
  fullPrompt = `Create a 4:5 Instagram carousel SLIDE ${slideNumber} for MSI Technologies.

LOGO: Use the attached MSI logo in TOP-LEFT corner, small size, white version. Do not modify it.

LAYOUT:
- Top 55%: Professional photo related to "${headline}", apply BLUE GRADIENT overlay at bottom of photo blending into blue section
- Bottom 45%: Solid MSI Blue #207CE5

TEXT (on blue section, left-aligned, white/cream):
- Headline (white): "${headline}"
- Body text (cream): "${bodyText}"
- Source (light cream, small): "Source: ${source}"

- Bottom-right: "${paginationNumber}/5"
- Bottom-left: ">>>"

STYLE: Clean, minimal, professional. No decorative elements.`;
}

return [{
  json: {
    image_prompt: fullPrompt,
    slide_id: input.slide_id,
    carousel_id: input.carousel_id,
    slide_number: slideNumber,
    headline: headline,
    subtext: subtext,
    body_text: bodyText,
    source: source,
    topic: topic,
    topic_tagline: topicTagline,
    is_blue_slide: isBlueSlide,
    logo_base64: logoBase64
  }
}];

