// Prepare Gemini Input - Build complete slide image prompt
const inputData = $('Webhook - Slide Gen').item.json;
const input = inputData.body || inputData;
const logoItem = $('Download MSI Logo').item;

// Get logo base64
let logoBase64 = '';
if (logoItem.binary && Object.keys(logoItem.binary).length > 0) {
  const binaryKey = Object.keys(logoItem.binary)[0];
  const binaryData = logoItem.binary[binaryKey];

  try {
    const buffer = await this.helpers.getBinaryDataBuffer(0, binaryKey);
    logoBase64 = buffer.toString('base64');
  } catch (e) {
     if (binaryData.data && binaryData.data !== 'filesystem-v2') {
         logoBase64 = binaryData.data;
     }
  }
}

// Extract all slide content fields
const slideNumber = parseInt(input.slide_number) || 1;
const headline = input.headline || '';
const subtext = input.subtext || '';
const bodyText = input.body_text || '';
const topic = input.topic || 'Technology';

// Extract source from visual_style JSON - CRITICAL: source is stored here
let source = '';
try {
  if (input.visual_style) {
    const vs = typeof input.visual_style === 'string' ? JSON.parse(input.visual_style) : input.visual_style;
    source = vs.source || '';
  }
} catch (e) {
  source = '';
}
// Fallback - if source is empty or just whitespace, use default
if (!source || source.trim() === '') {
  source = 'Industry Report';
}

// Generate dynamic tagline for Slide 1 based on topic
const topicLower = topic.toLowerCase();
let topicTagline = 'Our Latest Tech News';
if (topicLower.includes('ai') || topicLower.includes('artificial intelligence') || topicLower.includes('machine learning')) {
  topicTagline = 'Our Latest AI News';
} else if (topicLower.includes('security') || topicLower.includes('cyber')) {
  topicTagline = 'Our Latest Security News';
} else if (topicLower.includes('cloud')) {
  topicTagline = 'Our Latest Cloud News';
} else if (topicLower.includes('data')) {
  topicTagline = 'Our Latest Data News';
} else if (topicLower.includes('digital') || topicLower.includes('transformation')) {
  topicTagline = 'Our Latest Digital News';
} else if (topicLower.includes('bpo') || topicLower.includes('outsourcing')) {
  topicTagline = 'Our Latest Business News';
} else if (topicLower.includes('blockchain') || topicLower.includes('crypto')) {
  topicTagline = 'Our Latest Blockchain News';
} else if (topicLower.includes('automation') || topicLower.includes('rpa')) {
  topicTagline = 'Our Latest Automation News';
}

// Determine background color: Slides 1,3,5 = BLUE / Slides 2,4 = WHITE (alternating)
const isBlueSlide = (slideNumber === 1 || slideNumber === 3 || slideNumber === 5);
const isWhiteSlide = (slideNumber === 2 || slideNumber === 4);

// Build the COMPLETE image generation prompt based on slide type
// IMPORTANT: Do NOT include font specifications - Gemini renders them literally
let fullPrompt = '';

if (slideNumber === 1) {
  // COVER SLIDE - Blue background with photo and gradient
  fullPrompt = `Create a 4:5 Instagram carousel cover slide for MSI Technologies.

CRITICAL: Use ONLY the attached MSI logo in TOP-LEFT corner. Do NOT modify or recreate the logo.

DESIGN:
- Top 60%: Professional photo of modern tech office with screens, applying a blue gradient overlay (#207CE5) that blends into the bottom section
- Bottom 40%: Solid MSI Blue (#207CE5)
- The photo should seamlessly blend into the blue section via gradient

TEXT ON BLUE AREA (left-aligned, white text):
- Small tagline above: "${topicTagline}"
- Large prominent headline: "${headline}"
- Bottom-left: "Swipe >>>"

Logo: TOP-LEFT corner, use the attached logo exactly as provided.
Style: Clean, minimal, professional. No pagination on cover.`;

} else if (slideNumber === 5) {
  // CTA SLIDE - Blue background, NO photo, just solid blue with CTA
  fullPrompt = `Create a 4:5 Instagram carousel CTA slide for MSI Technologies.

CRITICAL: Use ONLY the attached MSI logo in TOP-LEFT corner. Do NOT modify or recreate the logo.

DESIGN:
- ENTIRE slide: Solid MSI Blue (#207CE5) background
- NO photo, NO image - just clean solid blue
- Centered content area

CONTENT (centered, white text):
- Large MSI logo in center (use the attached logo)
- CTA headline: "${headline}"
- Below the headline, add contact info with icons:
  * Email icon (envelope) + "contact@msitechnologiesinc.com"
  * Globe icon + "msitechnologiesinc.com"

Logo: TOP-LEFT corner (small) + CENTER (large), use attached logo exactly.
Bottom-left: "Follow Us"
Bottom-right: "5/5"
Style: Clean, minimal, professional call-to-action design.`;

} else if (isBlueSlide) {
  // CONTENT SLIDE with BLUE background (slide 3)
  fullPrompt = `Create a 4:5 Instagram carousel news slide for MSI Technologies.

CRITICAL: Use ONLY the attached MSI logo in TOP-LEFT corner. Do NOT modify or recreate the logo.

DESIGN:
- Top 55%: Professional photography related to "${headline}" with a blue gradient overlay (#207CE5) that fades into the bottom section
- Bottom 45%: Solid MSI Blue (#207CE5)
- The photo blends seamlessly into blue via gradient

TEXT ON BLUE AREA (left-aligned, white text):
- Headline: "${headline}"
- Body text (cream/light color): "${bodyText}"
- Source (small, light): "Source: ${source}"

Logo: TOP-LEFT corner, use attached logo exactly.
Bottom-left: ">>>"
Bottom-right: "${slideNumber}/5"
Style: Clean, minimal, professional news design.`;

} else {
  // CONTENT SLIDE with WHITE background (slides 2, 4)
  fullPrompt = `Create a 4:5 Instagram carousel news slide for MSI Technologies.

CRITICAL: Use ONLY the attached MSI logo in TOP-LEFT corner. Do NOT modify or recreate the logo.

DESIGN:
- Top 55%: Professional photography related to "${headline}" with a subtle blue tint
- Bottom 45%: Clean WHITE (#FFFFFF) background

TEXT ON WHITE AREA (left-aligned):
- Headline (MSI Blue #207CE5): "${headline}"
- Body text (dark gray): "${bodyText}"
- Source (small, gray): "Source: ${source}"

Logo: TOP-LEFT corner (use blue version of attached logo).
Bottom-left: ">>>" in gray
Bottom-right: "${slideNumber}/5" in gray
Style: Clean, minimal, professional news design.`;
}

return [{
  json: {
    image_prompt: fullPrompt,
    slide_id: input.slide_id,
    carousel_id: input.carousel_id,
    slide_number: slideNumber,
    headline: headline,
    subtext: subtext,
    body_text: bodyText,
    source: source,
    topic: topic,
    topic_tagline: topicTagline,
    is_blue_slide: isBlueSlide,
    logo_base64: logoBase64
  }
}];

