parameters.jsCode:
// Prepare Gemini Input - Build complete slide image prompt
const inputData = $('Webhook - Slide Gen').item.json;
const input = inputData.body || inputData;
const logoItem = $('Download MSI Logo').item;

// Get logo base64
let logoBase64 = '';
if (logoItem.binary && Object.keys(logoItem.binary).length > 0) {
  const binaryKey = Object.keys(logoItem.binary)[0];
  const binaryData = logoItem.binary[binaryKey];

  try {
    const buffer = await this.helpers.getBinaryDataBuffer(0, binaryKey);
    logoBase64 = buffer.toString('base64');
  } catch (e) {
     if (binaryData.data && binaryData.data !== 'filesystem-v2') {
         logoBase64 = binaryData.data;
     }
  }
}

// Extract all slide content fields
const slideNumber = parseInt(input.slide_number) || 1;
const headline = input.headline || '';
const subtext = input.subtext || '';
const bodyText = input.body_text || '';
const topic = input.topic || 'Technology';

// Extract source from visual_style JSON
let source = '';
try {
  if (input.visual_style) {
    const vs = typeof input.visual_style === 'string' ? JSON.parse(input.visual_style) : input.visual_style;
    source = vs.source || '';
  }
} catch (e) {
  source = '';
}

// Generate dynamic tagline for Slide 1 based on topic
const topicLower = topic.toLowerCase();
let topicTagline = 'Our Latest Tech News';
if (topicLower.includes('ai') || topicLower.includes('artificial intelligence') || topicLower.includes('machine learning')) {
  topicTagline = 'Our Latest AI News';
} else if (topicLower.includes('security') || topicLower.includes('cyber')) {
  topicTagline = 'Our Latest Security News';
} else if (topicLower.includes('cloud')) {
  topicTagline = 'Our Latest Cloud News';
} else if (topicLower.includes('data')) {
  topicTagline = 'Our Latest Data News';
} else if (topicLower.includes('digital') || topicLower.includes('transformation')) {
  topicTagline = 'Our Latest Digital News';
} else if (topicLower.includes('bpo') || topicLower.includes('outsourcing')) {
  topicTagline = 'Our Latest Business News';
} else if (topicLower.includes('blockchain') || topicLower.includes('crypto')) {
  topicTagline = 'Our Latest Blockchain News';
} else if (topicLower.includes('automation') || topicLower.includes('rpa')) {
  topicTagline = 'Our Latest Automation News';
}

// Determine background color: Slides 1,3,5 = BLUE / Slides 2,4 = WHITE
const isBlueSlide = (slideNumber === 1 || slideNumber === 3 || slideNumber === 5);
const bgColor = isBlueSlide ? '#207CE5' : '#FFFFFF';
const textColor = isBlueSlide ? '#FFFFFF' : '#2B2B2B';
const accentColor = isBlueSlide ? '#FFFFFF' : '#207CE5';

// Build the COMPLETE image generation prompt based on slide type
let fullPrompt = '';

if (slideNumber === 1) {
  // COVER SLIDE - Blue background, tagline above headline
  fullPrompt = `Create a professional Instagram carousel COVER slide (4:5 aspect ratio, 1080x1350 pixels) for MSI Technologies.

DESIGN SPECIFICATIONS:
- Background: MSI Blue (#207CE5) - solid color
- NO header bar at top
- Center area: Subtle abstract geometric shapes or gradients in lighter blue tones
- NO PAGINATION (no 1/5)
- IMPORTANT: Do NOT include any bullet points, news preview, topic list, or content summary

TEXT CONTENT (centered, from top to bottom):
1. Tagline (small, regular weight, white): \"${topicTagline}\"
2. Main Headline (large, bold, white): \"${headline}\"
- Both texts centered vertically

BRANDING:
- MSI Logo: White version, positioned at top left corner (small)
- Swipe indicator: Arrow pointing RIGHT + \"Swipe\" text in WHITE on the RIGHT side of slide (bottom area)

STYLE: Ultra-clean, minimalist. Blue background, white text. Professional tech brand feel.`;

} else if (slideNumber === 5) {
  // CTA SLIDE - Blue background
  fullPrompt = `Create a professional Instagram carousel CTA slide (4:5 aspect ratio, 1080x1350 pixels) for MSI Technologies.

DESIGN SPECIFICATIONS:
- Background: MSI Blue (#207CE5) - solid color
- NO header bar at top
- NO PAGINATION (no 5/5)

TEXT CONTENT (centered):
- CTA Headline (large, bold, white): \"${headline}\"

CONTACT SECTION (bottom, white text with white icons):
- Envelope icon + \"contact@msitechnologiesinc.com\"
- Globe icon + \"msitechnologiesinc.com\"

BRANDING:
- MSI Logo: White version, large, prominent, centered above CTA

STYLE: Clean, professional CTA design. Blue background, white elements.`;

} else if (slideNumber === 3) {
  // CONTENT SLIDE 3 - Blue background
  fullPrompt = `Create a professional Instagram carousel CONTENT slide (4:5 aspect ratio, 1080x1350 pixels) for MSI Technologies.

DESIGN SPECIFICATIONS:
- Background: MSI Blue (#207CE5) - solid color
- NO header bar - image goes edge to edge at top
- Layout: 55% top (visual area), 45% bottom (text area)
- PAGINATION: \"${slideNumber}/5\" in WHITE, top right corner (small, regular weight)

VISUAL AREA (top 55%):
- Professional imagery related to: ${headline}
- Image fills the entire top portion edge to edge
- Subtle blue tint overlay to blend with theme

TEXT AREA (bottom 45% - blue background continues):
- Headline: \"${headline}\" (bold, WHITE)
- Body: \"${bodyText}\" (regular, WHITE, max 3 lines)
- Source: \"Source: ${source}\" (small, light blue/white #B8D4F0)

BRANDING:
- MSI Logo: White version, bottom right, small

STYLE: Clean, professional news design. Blue theme throughout.`;

} else {
  // CONTENT SLIDES 2, 4 - White background
  fullPrompt = `Create a professional Instagram carousel CONTENT slide (4:5 aspect ratio, 1080x1350 pixels) for MSI Technologies.

DESIGN SPECIFICATIONS:
- Background: Clean WHITE (#FFFFFF)
- NO header bar - image goes edge to edge at top
- Layout: 55% top (visual area), 45% bottom (text area)
- PAGINATION: \"${slideNumber}/5\" in dark gray, top right corner (small, regular weight)

VISUAL AREA (top 55%):
- Professional imagery related to: ${headline}
- Image fills the entire top portion edge to edge
- NO blue bar, NO text overlay on image

TEXT AREA (bottom 45% - white background):
- Headline: \"${headline}\" (bold, MSI Blue #207CE5)
- Body: \"${bodyText}\" (regular, dark gray #2B2B2B, max 3 lines)
- Source: \"Source: ${source}\" (small, gray #888888)

BRANDING:
- MSI Logo: Blue version, bottom right, small

STYLE: Clean, professional news design. White background, blue accents.`;
}

return [{
  json: {
    image_prompt: fullPrompt,
    slide_id: input.slide_id,
    carousel_id: input.carousel_id,
    slide_number: slideNumber,
    headline: headline,
    subtext: subtext,
    body_text: bodyText,
    source: source,
    topic: topic,
    topic_tagline: topicTagline,
    logo_base64: logoBase64
  }
}];

