const input = $input.item.json;
let data = input.body || input;
if (typeof data === 'string') {
  try { data = JSON.parse(data); } catch (e) { }
}

const prompt = data.prompt || data.video_prompt || '';
const start_image_url = data.start_image_url || data.imageUrl || null;
const second_image_url = data.second_image_url || data.end_image_url || null;
const aspect_ratio = data.aspect_ratio || data.aspectRatio || '9:16';
const service = data.service || 'company_intro';
const post_id = data.post_id || data.postId || null;
const topic = data.topic || '';
const duration = Math.min(parseInt(data.duration || '8'), 8);

if (!prompt || prompt.length < 3) throw new Error('prompt is required');
if (!start_image_url) throw new Error('start_image_url is required');
if (!second_image_url || second_image_url.length < 5) throw new Error('second_image_url is required');

return [{ json: { prompt, duration: String(duration), aspect_ratio, service, post_id, topic, start_image_url, second_image_url } }];

const agentOutput = $('Agent: Video Script').item.json.output || '';
const f = $('Format Video Input').item.json;

let p1 = '', p2 = '';
if (agentOutput.includes('|||')) {
  const parts = agentOutput.split('|||');
  p1 = parts[0].trim();
  p2 = parts[1].trim();
} else {
  p1 = agentOutput.substring(0, 300);
  p2 = agentOutput.substring(0, 300);
}

// Validate word count in spoken dialogue (words after colon)
function countSpokenWords(prompt) {
  const match = prompt.match(/says?:\s*(.+?)(?:\.|!|\?|$)/i);
  if (!match) return 0;
  return match[1].trim().split(/\s+/).length;
}

const w1 = countSpokenWords(p1);
const w2 = countSpokenWords(p2);
if (w1 > 18) { console.log('WARNING: Part 1 has ' + w1 + ' spoken words (max 15)'); }
if (w2 > 18) { console.log('WARNING: Part 2 has ' + w2 + ' spoken words (max 15)'); }

// Minimal technical suffix - only essential directives
const suffix1 = ' Voice only, zero music, zero ambient sound. Static locked camera.';
const suffix2 = ' Person speaks immediately from first frame. Voice only, zero music, zero ambient sound. Static locked camera.';

return [{
  json: {
    PROMPT_PART1: (p1 + suffix1).substring(0, 500),
    PROMPT_PART2: (p2 + suffix2).substring(0, 500),
    DURATION: f.duration,
    ASPECT_RATIO: f.aspect_ratio,
    POST_ID: f.post_id,
    ORIGINAL_PROMPT: f.prompt,
    SERVICE: f.service,
    START_IMAGE_URL: f.start_image_url,
    SECOND_IMAGE_URL: f.second_image_url
  }
}];

// Download start image and base64 encode it (~15s max)
const s = $input.item.json;
const imgBuffer = await this.helpers.httpRequest({
  method: 'GET', url: s.START_IMAGE_URL, encoding: 'arraybuffer', timeout: 15000
});
const b64 = Buffer.from(imgBuffer).toString('base64');
if (b64.length < 100) throw new Error('Start image download failed');

return [{ json: { IMAGE_B64: b64, PROMPT_PART1: s.PROMPT_PART1, PROMPT_PART2: s.PROMPT_PART2, DURATION: s.DURATION, ASPECT_RATIO: s.ASPECT_RATIO, POST_ID: s.POST_ID, ORIGINAL_PROMPT: s.ORIGINAL_PROMPT, SERVICE: s.SERVICE, SECOND_IMAGE_URL: s.SECOND_IMAGE_URL } }];

