const input = $input.item.json;
let data = input.body || input;
if (typeof data === 'string') {
  try { data = JSON.parse(data); } catch (e) { }
}

const prompt = data.prompt || data.video_prompt || '';
const start_image_url = data.start_image_url || data.imageUrl || null;
const second_image_url = data.second_image_url || data.end_image_url || null;
const aspect_ratio = data.aspect_ratio || data.aspectRatio || '9:16';
const service = data.service || 'company_intro';
const post_id = data.post_id || data.postId || null;
const topic = data.topic || '';
const duration = Math.min(parseInt(data.duration || '8'), 8);

if (!prompt || prompt.length < 3) throw new Error('prompt is required');
if (!start_image_url) throw new Error('start_image_url is required');
if (!second_image_url || second_image_url.length < 5) throw new Error('second_image_url is required');

return [{ json: { prompt, duration: String(duration), aspect_ratio, service, post_id, topic, start_image_url, second_image_url } }];

const agentOutput = $('Agent: Video Script').item.json.output || '';
const formInput = $('Format Video Input').item.json;

let prompt1 = '';
let prompt2 = '';
if (agentOutput.includes('|||')) {
  const parts = agentOutput.split('|||');
  prompt1 = parts[0].trim();
  prompt2 = parts[1].trim();
} else {
  prompt1 = agentOutput.substring(0, 350);
  prompt2 = agentOutput.substring(0, 300) + ' Continue the scene with resolution.';
}

// ================================================
// IMPORTANT: Set your n8n webhook base URL here
// For self-hosted: http://localhost:5678
// For n8n cloud: https://your-instance.app.n8n.cloud
// ================================================
const N8N_BASE = 'http://localhost:5678';

return [{
  json: {
    PROMPT_PART1: prompt1 + ' The person speaks directly to camera with clear lip movement and audible voice. No text on screen.',
    PROMPT_PART2: prompt2 + ' The person speaks confidently to camera with clear lip movement and audible voice. No text on screen.',
    DURATION: formInput.duration,
    ASPECT_RATIO: formInput.aspect_ratio,
    POST_ID: formInput.post_id,
    ORIGINAL_PROMPT: formInput.prompt,
    START_IMAGE_URL: formInput.start_image_url,
    SECOND_IMAGE_URL: formInput.second_image_url,
    N8N_BASE: N8N_BASE
  }
}];

// Call the Video Job worker to submit Part 1 (download + base64 + token + veo)
// This keeps ALL heavy data (base64) in the worker's execution, not here
const s = $input.item.json;

const response = await this.helpers.httpRequest({
  method: 'POST',
  url: `${s.N8N_BASE}/webhook/msi-video-job`,
  headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify({
    action: 'submit',
    image_url: s.START_IMAGE_URL,
    prompt: s.PROMPT_PART1,
    bucket_path: 'part1/',
    duration: s.DURATION,
    aspect_ratio: s.ASPECT_RATIO
  }),
  timeout: 120000
});

if (!response.success || !response.operationName) {
  throw new Error('Submit Part 1 failed: ' + JSON.stringify(response).substring(0, 300));
}

// Pack lightweight state for after Wait (NO base64 anywhere!)
return [{
  json: {
    op1: response.operationName,
    PROMPT_PART2: s.PROMPT_PART2,
    DURATION: s.DURATION,
    ASPECT_RATIO: s.ASPECT_RATIO,
    POST_ID: s.POST_ID,
    ORIGINAL_PROMPT: s.ORIGINAL_PROMPT,
    SECOND_IMAGE_URL: s.SECOND_IMAGE_URL,
    N8N_BASE: s.N8N_BASE
  }
}];

// After Wait - fetch Part 1 result via the worker (fresh token generated there)
const s = $input.item.json;

const response = await this.helpers.httpRequest({
  method: 'POST',
  url: `${s.N8N_BASE}/webhook/msi-video-job`,
  headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify({
    action: 'fetch',
    operationName: s.op1
  }),
  timeout: 120000
});

if (!response.success || !response.ready) {
  throw new Error('Part 1 not ready: ' + JSON.stringify(response).substring(0, 300));
}

return [{
  json: {
    video1_gcs_uri: response.videoUri,
    PROMPT_PART2: s.PROMPT_PART2,
    DURATION: s.DURATION,
    ASPECT_RATIO: s.ASPECT_RATIO,
    POST_ID: s.POST_ID,
    ORIGINAL_PROMPT: s.ORIGINAL_PROMPT,
    SECOND_IMAGE_URL: s.SECOND_IMAGE_URL,
    N8N_BASE: s.N8N_BASE
  }
}];

// Call the Video Job worker to submit Part 2
const s = $input.item.json;

const response = await this.helpers.httpRequest({
  method: 'POST',
  url: `${s.N8N_BASE}/webhook/msi-video-job`,
  headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify({
    action: 'submit',
    image_url: s.SECOND_IMAGE_URL,
    prompt: s.PROMPT_PART2,
    bucket_path: 'part2/',
    duration: s.DURATION,
    aspect_ratio: s.ASPECT_RATIO
  }),
  timeout: 120000
});

if (!response.success || !response.operationName) {
  throw new Error('Submit Part 2 failed: ' + JSON.stringify(response).substring(0, 300));
}

// Pack lightweight state for after Wait 2
return [{
  json: {
    op2: response.operationName,
    video1_gcs_uri: s.video1_gcs_uri,
    DURATION: s.DURATION,
    POST_ID: s.POST_ID,
    ORIGINAL_PROMPT: s.ORIGINAL_PROMPT,
    N8N_BASE: s.N8N_BASE
  }
}];

// After Wait - fetch Part 2 result via the worker
const s = $input.item.json;

const response = await this.helpers.httpRequest({
  method: 'POST',
  url: `${s.N8N_BASE}/webhook/msi-video-job`,
  headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify({
    action: 'fetch',
    operationName: s.op2
  }),
  timeout: 120000
});

if (!response.success || !response.ready) {
  throw new Error('Part 2 not ready: ' + JSON.stringify(response).substring(0, 300));
}

return [{
  json: {
    video1_gcs_uri: s.video1_gcs_uri,
    video2_gcs_uri: response.videoUri,
    POST_ID: s.POST_ID,
    ORIGINAL_PROMPT: s.ORIGINAL_PROMPT,
    DURATION: s.DURATION
  }
}];
