// Generate V4 Signed URLs so browser can download videos (~5s max)
const crypto = require('crypto');
const s = $input.item.json;
const creds = $('Publicar Credenciales').item.json;
const SA_EMAIL = creds.SA_EMAIL;
const SA_KEY = creds.SA_KEY;

function signUrl(gsUri) {
  const m = gsUri.match(/^gs:\/\/([^\/]+)\/(.+)$/);
  if (!m) return gsUri;
  const bucket = m[1], objPath = m[2], host = 'storage.googleapis.com';
  const d = new Date();
  const pad = v => String(v).padStart(2, '0');
  const ds = d.getUTCFullYear() + '' + pad(d.getUTCMonth()+1) + pad(d.getUTCDate());
  const dt = ds + 'T' + pad(d.getUTCHours()) + pad(d.getUTCMinutes()) + pad(d.getUTCSeconds()) + 'Z';
  const scope = ds + '/auto/storage/goog4_request';
  const cred = SA_EMAIL + '/' + scope;
  const qs = 'X-Goog-Algorithm=GOOG4-RSA-SHA256&X-Goog-Credential=' + encodeURIComponent(cred) + '&X-Goog-Date=' + dt + '&X-Goog-Expires=604800&X-Goog-SignedHeaders=host';
  const objEnc = objPath.split('/').map(p => encodeURIComponent(p)).join('/');
  const canon = 'GET\n/' + bucket + '/' + objEnc + '\n' + qs + '\nhost:' + host + '\n\nhost\nUNSIGNED-PAYLOAD';
  const sts = 'GOOG4-RSA-SHA256\n' + dt + '\n' + scope + '\n' + crypto.createHash('sha256').update(canon).digest('hex');
  const sg = crypto.createSign('RSA-SHA256');
  sg.update(sts);
  return 'https://' + host + '/' + bucket + '/' + objEnc + '?' + qs + '&X-Goog-Signature=' + sg.sign(SA_KEY, 'hex');
}

