const input = $input.item.json;
let data = input.body || input;
if (typeof data === 'string') {
  try { data = JSON.parse(data); } catch (e) { }
}

const prompt = data.prompt || data.video_prompt || '';
const start_image_url = data.start_image_url || data.imageUrl || null;
const second_image_url = data.second_image_url || data.end_image_url || null;
const aspect_ratio = data.aspect_ratio || data.aspectRatio || '9:16';
const service = data.service || 'company_intro';
const post_id = data.post_id || data.postId || null;
const topic = data.topic || '';

// Veo 3.1: max 8s por video, siempre 2 partes
const duration = Math.min(parseInt(data.duration || '8'), 8);

if (!prompt || prompt.length < 3) {
  throw new Error('prompt is required (min 3 chars)');
}
if (!start_image_url) {
  throw new Error('start_image_url is required');
}
if (!second_image_url || second_image_url.length < 5) {
  throw new Error('second_image_url is required for part 2');
}

return [{ 
  json: { 
    prompt, 
    duration: String(duration),
    aspect_ratio, 
    service,
    post_id, 
    topic,
    start_image_url,
    second_image_url
  } 
}];

const imgData = $input.item.json;
const settings = $('Settings').item.json;

if (!imgData.data || imgData.data.length < 100) {
  throw new Error('Start image base64 is empty or too small');
}

return [{
  json: {
    ...settings,
    START_IMAGE_B64: imgData.data,
    START_MIME: imgData.mimeType || 'image/jpeg'
  }
}];

