const response = $input.item.json;
const settings = $('Generate Access Token').item.json;

let videoUri = '';
if (response.response?.videos?.[0]?.uri) {
  videoUri = response.response.videos[0].uri;
} else if (response.response?.videos?.[0]?.gcsUri) {
  videoUri = response.response.videos[0].gcsUri;
}

if (!videoUri) {
  throw new Error('Video Part 1 failed: ' + JSON.stringify(response).substring(0, 500));
}

return [{
  json: {
    video1_gcs_uri: videoUri,
    ...settings
  }
}];

const imgData = $input.item.json;
const settings = $('Process Part 1').item.json;

if (!imgData.data || imgData.data.length < 100) {
  throw new Error('Second image base64 is empty');
}

return [{
  json: {
    ...settings,
    SECOND_IMAGE_B64: imgData.data,
    SECOND_MIME: imgData.mimeType || 'image/jpeg'
  }
}];

