const response = $input.item.json;
const settings = $('Prepare Part 2').item.json;

let video2Uri = '';
if (response.response?.videos?.[0]?.uri) {
  video2Uri = response.response.videos[0].uri;
} else if (response.response?.videos?.[0]?.gcsUri) {
  video2Uri = response.response.videos[0].gcsUri;
}

if (!video2Uri) {
  throw new Error('Video Part 2 failed: ' + JSON.stringify(response).substring(0, 500));
}

return [{
  json: {
    video1_gcs_uri: settings.video1_gcs_uri,
    video2_gcs_uri: video2Uri,
    post_id: settings.POST_ID,
    original_prompt: settings.ORIGINAL_PROMPT,
    ACCESS_TOKEN: settings.ACCESS_TOKEN,
    BUCKET_NAME: settings.BUCKET_NAME,
    DURATION: settings.DURATION
  }
}];

const settings = $('Process Part 2').item.json;
const binary = $input.item.binary;

if (!binary || !binary.data) {
  throw new Error('No video binary data');
}

const prompt = settings.original_prompt || 'MSI Content';
const totalDuration = (parseInt(settings.DURATION) * 2) + 's (2 parts)';

return {
  json: {
    video1_binary_size: binary.data.length,
    video2_gcs_uri: settings.video2_gcs_uri,
    post_id: settings.post_id,
    original_prompt: prompt,
    duration: totalDuration,
    ACCESS_TOKEN: settings.ACCESS_TOKEN,
    BUCKET_NAME: settings.BUCKET_NAME
  },
  binary: binary
};
