// Extract data for image animation
const input = $input.item.json;

let data;
if (input.body) {
  if (typeof input.body === 'string') {
    try { data = JSON.parse(input.body); } catch (e) { data = input; }
  } else if (typeof input.body === 'object') {
    data = input.body;
  } else { data = input; }
} else if (input.query) {
  data = input.query;
} else {
  data = input;
}

const base_image_url = data.base_image_url || data.image_url || '';
const prompt = data.prompt || data.animation_prompt || 'Minimal motion: subtle parallax depth only, no zoom, people frozen';
const duration = data.duration || '8';
const aspect_ratio = data.aspect_ratio || '9:16';
const post_id = data.post_id || null;

if (!base_image_url) {
  throw new Error('IMAGE ANIMATION ERROR: base_image_url is required.');
}

return [{ json: { base_image_url, prompt, duration, aspect_ratio, post_id } }];

// Merge settings with base image data
const settings = $('Settings').item.json;
const imageData = $input.item.json;

return [{
  json: {
    ...settings,
    IMAGE_BASE64: imageData.data,
    IMAGE_MIME_TYPE: imageData.mimeType || 'image/png'
  }
}];

