// Process Vertex AI response
const response = $input.item.json;
const settings = $('Generate Access Token').item.json;

console.log('==== PROCESS VEO3 RESPONSE ====');
console.log('Response keys:', Object.keys(response));

// Check if operation is still running
if (response.done === false) {
  return [{
    json: {
      is_async: true,
      operation_name: $('Vertex AI - VEO3').item.json.name,
      status: 'still_processing',
      post_id: settings.POST_ID,
      prompt: settings.TEXT_PROMPT,
      original_prompt: settings.ORIGINAL_PROMPT,
      message: 'Video still processing. Try fetching again later.'
    }
  }];
}

let videoBase64 = '';
let mimeType = 'video/mp4';

// Try different response formats from Veo 3.0
if (response.response?.videos?.[0]?.bytesBase64Encoded) {
  videoBase64 = response.response.videos[0].bytesBase64Encoded;
  mimeType = response.response.videos[0].mimeType || 'video/mp4';
} else if (response.response?.generatedSamples?.[0]?.video?.bytesBase64Encoded) {
  videoBase64 = response.response.generatedSamples[0].video.bytesBase64Encoded;
} else if (response.predictions?.[0]?.bytesBase64Encoded) {
  videoBase64 = response.predictions[0].bytesBase64Encoded;
} else if (response.generatedSamples?.[0]?.video?.bytesBase64Encoded) {
  videoBase64 = response.generatedSamples[0].video.bytesBase64Encoded;
}

if (!videoBase64) {
  console.log('Full response:', JSON.stringify(response));
  throw new Error('No video data found. Response: ' + JSON.stringify(response).substring(0, 1000));
}

console.log('Video extracted successfully, size:', videoBase64.length);
console.log('==== END PROCESS ====');

return [{
  json: {
    video_base64: videoBase64,
    video_data_url: `data:${mimeType};base64,${videoBase64}`,
    mime_type: mimeType,
    has_video: true,
    is_async: false,
    post_id: settings.POST_ID,
    prompt: settings.TEXT_PROMPT,
    original_prompt: settings.ORIGINAL_PROMPT
  }
}];

// Convert base64 video to binary for YouTube upload
const videoBase64 = $json.video_base64;
const prompt = $json.animation_prompt || 'MSI Animated Content';
const postId = $json.post_id;
const mimeType = $json.mime_type || 'video/mp4';

if (!videoBase64) {
  throw new Error('No video base64 data available');
}

const binaryData = Buffer.from(videoBase64, 'base64');
let title = prompt.substring(0, 97);
if (prompt.length > 97) title += '...';
const fileName = `msi_video_${Date.now()}.mp4`;

return {
  json: {
    title,
    description: `Animated with Vertex AI Veo 3.1 by MSI Technologies\n\nAnimation: ${prompt}\n\nPost ID: ${postId || 'N/A'}`,
    post_id: postId,
    original_prompt: prompt
  },
  binary: {
    data: {
      data: binaryData.toString('base64'),
      mimeType: mimeType,
      fileName: fileName
    }
  }
};

const response = $input.item.json;
const preparedData = $('Prepare YouTube Upload').item.json;

const videoId = response.id || response.uploadId;
const youtubeUrl = `https://youtu.be/${videoId}`;

return [{
  json: {
    youtube_url: youtubeUrl,
    youtube_video_id: videoId,
    post_id: preparedData.post_id,
    title: preparedData.title,
    animation_prompt: preparedData.original_prompt
  }
}];
