const input = $input.item.json;
let data = input.body || input;
if (typeof data === 'string') {
  try { data = JSON.parse(data); } catch (e) { }
}

const prompt = data.prompt || data.video_prompt || '';
const start_image_url = data.start_image_url || data.imageUrl || null;
const second_image_url = data.second_image_url || data.end_image_url || null;
const aspect_ratio = data.aspect_ratio || data.aspectRatio || '9:16';
const service = data.service || 'company_intro';
const post_id = data.post_id || data.postId || null;
const topic = data.topic || '';

const duration = Math.min(parseInt(data.duration || '8'), 8);

if (!prompt || prompt.length < 3) {
  throw new Error('prompt is required (min 3 chars)');
}
if (!start_image_url) {
  throw new Error('start_image_url is required');
}
if (!second_image_url || second_image_url.length < 5) {
  throw new Error('second_image_url is required for part 2');
}

return [{ 
  json: { 
    prompt, 
    duration: String(duration),
    aspect_ratio, 
    service,
    post_id, 
    topic,
    start_image_url,
    second_image_url
  } 
}];

const agentOutput = $('Agent: Video Script').item.json.output || '';
const formInput = $('Format Video Input').item.json;

// Parse the two prompts from agent output
let prompt1 = '';
let prompt2 = '';

if (agentOutput.includes('|||')) {
  const parts = agentOutput.split('|||');
  prompt1 = parts[0].trim();
  prompt2 = parts[1].trim();
} else {
  // Fallback: use the whole output for both with different endings
  prompt1 = agentOutput.substring(0, 350);
  prompt2 = agentOutput.substring(0, 300) + ' Continue the scene with resolution.';
}

return [{
  json: {
    PROMPT_PART1: prompt1,
    PROMPT_PART2: prompt2,
    PROJECT_ID: 'gen-lang-client-0521438560',
    MODEL_VERSION: 'veo-3.1-generate-001',
    LOCATION: 'us-central1',
    API_ENDPOINT: 'us-central1-aiplatform.googleapis.com',
    BUCKET_NAME: 'msi-veo-videos',
    DURATION: formInput.duration,
    ASPECT_RATIO: formInput.aspect_ratio,
    POST_ID: formInput.post_id,
    ORIGINAL_PROMPT: formInput.prompt,
    START_IMAGE_URL: formInput.start_image_url,
    SECOND_IMAGE_URL: formInput.second_image_url
  }
}];

const imgData = $input.item.json;
const settings = $('Build Settings').item.json;

if (!imgData.data || imgData.data.length < 100) {
  throw new Error('Start image base64 is empty or too small');
}

return [{
  json: {
    ...settings,
    START_IMAGE_B64: imgData.data,
    START_MIME: imgData.mimeType || 'image/jpeg'
  }
}];

