const settings = $input.item.json;

function base64url(str) {
  return Buffer.from(str).toString('base64').replace(/\+/g, '-').replace(/\//g, '_').replace(/=/g, '');
}
function base64urlBuffer(buffer) {
  return buffer.toString('base64').replace(/\+/g, '-').replace(/\//g, '_').replace(/=/g, '');
}

const now = Math.floor(Date.now() / 1000);
const header = { alg: 'RS256', typ: 'JWT' };
const payload = {
  iss: serviceAccount.client_email,
  sub: serviceAccount.client_email,
  aud: serviceAccount.token_uri,
  iat: now,
  exp: now + 3600,
  scope: 'https://www.googleapis.com/auth/cloud-platform'
};

const headerB64 = base64url(JSON.stringify(header));
const payloadB64 = base64url(JSON.stringify(payload));
const signatureInput = `${headerB64}.${payloadB64}`;
const sign = crypto.createSign('RSA-SHA256');
sign.update(signatureInput);
const signature = sign.sign(serviceAccount.private_key);
const jwt = `${signatureInput}.${base64urlBuffer(signature)}`;

const tokenResponse = await this.helpers.httpRequest({
  method: 'POST',
  url: serviceAccount.token_uri,
  headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
  body: `grant_type=urn:ietf:params:oauth:grant-type:jwt-bearer&assertion=${jwt}`
});

if (!tokenResponse.access_token) {
  throw new Error('Failed to get access token');
}

return [{
  json: {
    ...settings,
    ACCESS_TOKEN: tokenResponse.access_token
  }
}];

// Send Veo request for Part 1 AND save all state for after Wait
const s = $input.item.json;

const requestBody = {
  instances: [{
    prompt: s.PROMPT_PART1 + ' The person speaks directly to camera with clear lip movement. No text on screen.',
    image: {
      bytesBase64Encoded: s.START_IMAGE_B64,
      mimeType: s.START_MIME
    }
  }],
  parameters: {
    aspectRatio: s.ASPECT_RATIO,
    resolution: '720p',
    sampleCount: 1,
    durationSeconds: parseInt(s.DURATION),
    personGeneration: 'allow_all',
    addWatermark: false,
    includeRaiReason: true,
    generateAudio: true,
    storageUri: 'gs://' + s.BUCKET_NAME + '/part1/',
    negativePrompt: 'silence, no audio, mute, static, blurry, text, letters, words, subtitles, captions, watermark'
  }
};

const response = await this.helpers.httpRequest({
  method: 'POST',
  url: `https://${s.API_ENDPOINT}/v1/projects/${s.PROJECT_ID}/locations/${s.LOCATION}/publishers/google/models/${s.MODEL_VERSION}:predictLongRunning`,
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${s.ACCESS_TOKEN}`
  },
  body: JSON.stringify(requestBody),
  timeout: 30000
});

if (!response.name) {
  throw new Error('Vertex AI Part 1 failed: ' + JSON.stringify(response).substring(0, 500));
}

// CRITICAL: Pack ALL state into the item so it survives the Wait node
return [{
  json: {
    operationName: response.name,
    API_ENDPOINT: s.API_ENDPOINT,
    PROJECT_ID: s.PROJECT_ID,
    LOCATION: s.LOCATION,
    MODEL_VERSION: s.MODEL_VERSION,
    ACCESS_TOKEN: s.ACCESS_TOKEN,
    BUCKET_NAME: s.BUCKET_NAME,
    DURATION: s.DURATION,
    ASPECT_RATIO: s.ASPECT_RATIO,
    POST_ID: s.POST_ID,
    ORIGINAL_PROMPT: s.ORIGINAL_PROMPT,
    PROMPT_PART2: s.PROMPT_PART2,
    SECOND_IMAGE_URL: s.SECOND_IMAGE_URL
  }
}];

// After Wait resume - ALL data is in $json (packed before Wait)
const s = $input.item.json;

const response = await this.helpers.httpRequest({
  method: 'POST',
  url: `https://${s.API_ENDPOINT}/v1/projects/${s.PROJECT_ID}/locations/${s.LOCATION}/publishers/google/models/${s.MODEL_VERSION}:fetchPredictOperation`,
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${s.ACCESS_TOKEN}`
  },
  body: JSON.stringify({ operationName: s.operationName }),
  timeout: 60000
});

let videoUri = '';
if (response.response?.videos?.[0]?.uri) {
  videoUri = response.response.videos[0].uri;
} else if (response.response?.videos?.[0]?.gcsUri) {
  videoUri = response.response.videos[0].gcsUri;
}

if (!videoUri) {
  throw new Error('Video Part 1 not ready or failed: ' + JSON.stringify(response).substring(0, 500));
}

// Pass everything forward including video1 URI
return [{
  json: {
    video1_gcs_uri: videoUri,
    API_ENDPOINT: s.API_ENDPOINT,
    PROJECT_ID: s.PROJECT_ID,
    LOCATION: s.LOCATION,
    MODEL_VERSION: s.MODEL_VERSION,
    ACCESS_TOKEN: s.ACCESS_TOKEN,
    BUCKET_NAME: s.BUCKET_NAME,
    DURATION: s.DURATION,
    ASPECT_RATIO: s.ASPECT_RATIO,
