};

const response = await this.helpers.httpRequest({
  method: 'POST',
  url: `https://${settings.API_ENDPOINT}/v1/projects/${settings.PROJECT_ID}/locations/${settings.LOCATION}/publishers/google/models/${settings.MODEL_VERSION}:predictLongRunning`,
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${settings.ACCESS_TOKEN}`
  },
  body: JSON.stringify(requestBody),
  timeout: 30000
});

if (!response.name) {
  throw new Error('Vertex AI Part 2 failed: ' + JSON.stringify(response).substring(0, 500));
}

// Pack ALL state for after Wait Part 2
return [{
  json: {
    operationName: response.name,
    video1_gcs_uri: settings.video1_gcs_uri,
    API_ENDPOINT: settings.API_ENDPOINT,
    PROJECT_ID: settings.PROJECT_ID,
    LOCATION: settings.LOCATION,
    MODEL_VERSION: settings.MODEL_VERSION,
    ACCESS_TOKEN: settings.ACCESS_TOKEN,
    BUCKET_NAME: settings.BUCKET_NAME,
    DURATION: settings.DURATION,
    POST_ID: settings.POST_ID,
    ORIGINAL_PROMPT: settings.ORIGINAL_PROMPT
  }
}];

// After Wait resume - ALL data is in $json
const s = $input.item.json;

const response = await this.helpers.httpRequest({
  method: 'POST',
  url: `https://${s.API_ENDPOINT}/v1/projects/${s.PROJECT_ID}/locations/${s.LOCATION}/publishers/google/models/${s.MODEL_VERSION}:fetchPredictOperation`,
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${s.ACCESS_TOKEN}`
  },
  body: JSON.stringify({ operationName: s.operationName }),
  timeout: 60000
});

let video2Uri = '';
if (response.response?.videos?.[0]?.uri) {
  video2Uri = response.response.videos[0].uri;
} else if (response.response?.videos?.[0]?.gcsUri) {
  video2Uri = response.response.videos[0].gcsUri;
}

if (!video2Uri) {
