// Extract data from web form for video generation
const input = $input.item.json;

console.log('==== VIDEO GEN WEBHOOK INPUT ====');
console.log('Raw input:', JSON.stringify(input));

// Parse the body if it's a JSON string
let data;
if (input.body) {
  if (typeof input.body === 'string') {
    try {
      data = JSON.parse(input.body);
    } catch (e) {
      data = input;
    }
  } else if (typeof input.body === 'object') {
    data = input.body;
  } else {
    data = input;
  }
} else if (input.query) {
  data = input.query;
} else {
  data = input;
}

// Extract variables for video generation
const prompt = data.prompt || data.video_prompt || '';
const duration = data.duration || '5s';
const aspect_ratio = data.aspect_ratio || '9:16';
const style = data.style || 'cinematic';
const reference_image_url = data.reference_image_url || null;
const post_id = data.post_id || null;
const topic = data.topic || '';

console.log('Variables:', { prompt, duration, aspect_ratio, style, reference_image_url, post_id, topic });

if (!prompt) {
  throw new Error('VIDEO GEN ERROR: prompt is required for video generation.');
}

return [{
  json: {
    prompt,
    duration,
    aspect_ratio,
    style,
    reference_image_url,
    post_id,
    topic
  }
}];

// Prepare data for Veo 3.1 video generation
const formInput = $('Format Video Input').item.json;
const optimizedPrompt = $input.item.json.output;

console.log('==== PREPARE VIDEO GEN REQUEST ====');
console.log('Optimized prompt:', optimizedPrompt);

// Append duration and vertical format instructions
const promptWithEnhancements = `${optimizedPrompt}. ${formInput.duration} video, STRICTLY VERTICAL 9:16 portrait orientation, with smooth fade out ending. Include watermark logo in bottom right corner throughout the video.`;

console.log('Enhanced prompt:', promptWithEnhancements);

return [{
  json: {
    prompt: promptWithEnhancements,
    original_prompt: formInput.prompt,
    duration: formInput.duration,
    aspect_ratio: '9:16',
    style: formInput.style,
    post_id: formInput.post_id,
    topic: formInput.topic
  }
}];

// Merge video request data with logo binary and convert to base64
const videoData = $('Prepare Video Request').item.json;
const logoItem = $input.item;

console.log('==== MERGE VIDEO DATA WITH LOGO ====');
console.log('Has logo binary:', !!logoItem.binary);
console.log('Logo item keys:', Object.keys(logoItem));

let logoBase64 = '';
let logoMimeType = 'image/png';

if (logoItem.binary && Object.keys(logoItem.binary).length > 0) {
  const binaryKey = Object.keys(logoItem.binary)[0];
  const binaryData = logoItem.binary[binaryKey];
  logoMimeType = binaryData.mimeType || 'image/png';

  console.log('Binary key:', binaryKey);
  console.log('Binary data keys:', Object.keys(binaryData));
  console.log('MIME type:', logoMimeType);

  try {
    // Use index 0 directly, not from json
    const buffer = await this.helpers.getBinaryDataBuffer(0, binaryKey);
    logoBase64 = buffer.toString('base64');
    console.log('Logo base64 extracted successfully, length:', logoBase64.length);
  } catch (e) {
    console.error('Failed to get logo buffer with helpers:', e.message);
    // Fallback: try direct access
    if (binaryData.data && typeof binaryData.data === 'string' && binaryData.data !== 'filesystem-v2') {
      logoBase64 = binaryData.data;
      console.log('Used fallback direct data access');
    } else if (Buffer.isBuffer(binaryData.data)) {
      logoBase64 = binaryData.data.toString('base64');
      console.log('Converted buffer to base64');
    }
  }
}

if (!logoBase64) {
  console.error('No logo data extracted!');
  // Use a fallback: hardcoded small transparent PNG
  logoBase64 = 'iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mNk+M9QDwADhgGAWjR9awAAAABJRU5ErkJggg==';
  console.log('Using fallback transparent PNG');
}

console.log('Final logo base64 length:', logoBase64.length);
console.log('Final MIME type:', logoMimeType);

return [{
  json: {
    prompt: videoData.prompt,
    original_prompt: videoData.original_prompt,
    duration: videoData.duration,
    aspect_ratio: videoData.aspect_ratio,
    style: videoData.style,
    post_id: videoData.post_id,
    topic: videoData.topic,
    logo_base64: logoBase64,
    logo_mime_type: logoMimeType
  }
}];

// Process Veo 3.1 HTTP response - handles both sync and async responses
const item = $input.item;
const response = item.json;
const preparedData = $('Prepare Video Request').item.json;

console.log('==== VEO 3.1 HTTP RESPONSE ====');
console.log('Response keys:', Object.keys(response));
console.log('Response:', JSON.stringify(response, null, 2).substring(0, 2000));

let videoBase64 = '';
let mimeType = 'video/mp4';
let operationName = '';

try {
  // Check for Long-Running Operation response
  if (response.name && response.name.includes('operations/')) {
    operationName = response.name;
    console.log('Got async operation:', operationName);
    // For now, we'll throw and need to implement polling
    // In production, you'd poll GET /v1beta/{operationName} until done
  }

  // Check for completed operation with predictions
  if (response.predictions && response.predictions.length > 0) {
    const prediction = response.predictions[0];
    if (prediction.video) {
      videoBase64 = prediction.video.bytesBase64Encoded || prediction.video;
      mimeType = prediction.video.mimeType || 'video/mp4';
      console.log('Found video in predictions');
    } else if (prediction.bytesBase64Encoded) {
      videoBase64 = prediction.bytesBase64Encoded;
    }
  }

  // Check for generatedSamples (Imagen-style response)
  if (!videoBase64 && response.generatedSamples && response.generatedSamples.length > 0) {
    const sample = response.generatedSamples[0];
    videoBase64 = sample.video?.bytesBase64Encoded || sample.bytesBase64Encoded || '';
    mimeType = sample.video?.mimeType || sample.mimeType || 'video/mp4';
  }

  // Check for candidates (Gemini-style)
  if (!videoBase64 && response.candidates && response.candidates.length > 0) {
    const candidate = response.candidates[0];
    if (candidate.content && candidate.content.parts) {
      for (const part of candidate.content.parts) {
        const inlineData = part.inlineData || part.inline_data;
        if (inlineData && inlineData.data) {
          videoBase64 = inlineData.data;
          mimeType = inlineData.mimeType || inlineData.mime_type || 'video/mp4';
          break;
        }
      }
    }
  }

  // Check for direct video field
  if (!videoBase64 && response.video) {
    videoBase64 = response.video.bytesBase64Encoded || response.video.data || response.video;
    mimeType = response.video.mimeType || 'video/mp4';
  }

} catch (e) {
  console.error('Error parsing Veo response:', e.message);
  throw e;
}

// If we got an operation name but no video, we need to poll
if (operationName && !videoBase64) {
  // Return operation info for manual polling or next step
  return [{
    json: {
      is_async: true,
      operation_name: operationName,
      status: 'pending',
      post_id: preparedData.post_id,
      prompt: preparedData.prompt,
      original_prompt: preparedData.original_prompt,
      message: 'Video generation started. Operation: ' + operationName
    }
  }];
}

console.log('Has video base64:', !!videoBase64, videoBase64 ? `(${videoBase64.length} chars)` : '');

if (!videoBase64) {
  console.log('Full response:', JSON.stringify(response, null, 2));
  throw new Error('No video data found. Response: ' + JSON.stringify(response).substring(0, 500));
}

const videoDataUrl = `data:${mimeType};base64,${videoBase64}`;

return [{
  json: {
    video_base64: videoBase64,
    video_data_url: videoDataUrl,
    mime_type: mimeType,
    has_video: true,
    is_async: false,
    post_id: preparedData.post_id,
    prompt: preparedData.prompt,
    original_prompt: preparedData.original_prompt
  }
}];

// Convert base64 video to binary for YouTube upload
const videoBase64 = $json.video_base64;
const prompt = $json.prompt || 'MSI Content';
const postId = $json.post_id;
const mimeType = $json.mime_type || 'video/mp4';

if (!videoBase64) {
  throw new Error('No video base64 data available for YouTube upload');
}

// Convert base64 to binary buffer
const binaryData = Buffer.from(videoBase64, 'base64');

// Generate title (truncate if too long, YouTube max is 100 chars)
let title = prompt.substring(0, 97);
if (prompt.length > 97) title += '...';

// Generate a filename
const fileName = `msi_video_${Date.now()}.mp4`;

console.log('Preparing YouTube upload:', {
  title,
  dataSize: binaryData.length,
  postId,
  fileName
});

// Return with proper binary format for n8n YouTube node
return {
  json: {
    title,
    description: `Generated with AI\n\nPrompt: ${prompt}\n\nPost ID: ${postId || 'N/A'}`,
    post_id: postId,
    original_prompt: prompt
  },
  binary: {
    data: {
      data: binaryData.toString('base64'),
      mimeType: mimeType,
      fileName: fileName
    }
  }
};

// Extract YouTube URL from response
const response = $input.item.json;
const preparedData = $('Prepare YouTube Upload').item.json;

console.log('==== YOUTUBE UPLOAD RESPONSE ====');
console.log('Response keys:', Object.keys(response));

// YouTube response structure: { id: 'video_id', uploadId: 'video_id', snippet: { ... }, status: { ... } }
const videoId = response.id || response.uploadId;
const youtubeUrl = `https://youtu.be/${videoId}`;

console.log('YouTube URL:', youtubeUrl);
console.log('Video ID:', videoId);

return [{
  json: {
    youtube_url: youtubeUrl,
    youtube_video_id: videoId,
    post_id: preparedData.post_id,
    title: preparedData.title,
    prompt: preparedData.original_prompt
  }
}];
